import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IRefund } from "@ORGANIZATION/PROJECT-api/lib/structures/IRefund";

/**
 * Validate updating an existing refund record with valid mutation fields.
 *
 * This scenario tests the update logic for refund records in the payment discount domain. The test ensures that updating fields such as refund reason or administrative notes on a refund record successfully persists the changes.
 *
 * The test first ensures that a refund record exists by creating one via the designated API. It then attempts to update allowed fields, confirms those fields are actually mutated, and finally verifies that the persisted record matches expectations.
 *
 * 1. Create a refund record (to be updated)
 * 2. Update the refund record with new valid fields (e.g., reason, amount)
 * 3. Confirm that the updated record reflects the changes
 */
export async function test_api_refund_update_with_valid_fields(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a refund record
  const createInput: IRefund.ICreate = {
    payment_id: typia.random<string>(),
    transaction_id: typia.random<string>(),
    order_id: null,
    amount: 10000,
    currency: "KRW",
    status: "requested",
    reason: "Initial refund for test."
  };
  const created: IRefund = await api.functional.paymentsDiscounts.refunds.post(
    connection,
    { body: createInput },
  );
  typia.assert(created);

  // 2. Update the refund record with new valid fields
  const updateInput: IRefund.IUpdate = {
    reason: "Changed reason for evidence.",
    amount: 11000,
    status: "completed",
    completed_at: new Date().toISOString(),
  };
  const updated: IRefund = await api.functional.paymentsDiscounts.refunds.putById(
    connection,
    {
      id: created.id,
      body: updateInput,
    }
  );
  typia.assert(updated);

  // 3. Confirm that the updated record reflects the changes
  TestValidator.equals("updated id")(created.id)(updated.id);
  TestValidator.equals("updated amount")(updateInput.amount)(updated.amount);
  TestValidator.equals("updated reason")(updateInput.reason)(updated.reason);
  TestValidator.equals("updated status")(updateInput.status)(updated.status);
  TestValidator.equals("updated completed_at")(updateInput.completed_at)(updated.completed_at);
}

