import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IPageIPaymentAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIPaymentAudit";
import { IPaymentAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IPaymentAudit";

/**
 * Retrieve a paginated and filtered list of payment audit events.
 *
 * This test covers listing of payment audit logs using pagination and various filters.
 * To ensure audit events exist for querying, at least one payment audit log is created first.
 *
 * Steps:
 * 1. Create a payment audit event (minimal required).
 * 2. List payment audit events using default/no filter (should at least see the created entry).
 * 3. Search for specific audit event by exact entity_id and entity_type; expect 1 match.
 * 4. Search using a filter known to return no results (e.g., invalid action string).
 * 5. Paginate with excessive page size (e.g., limit=1000) and validate returned limit and total count metadata.
 */
export async function test_api_payment_audit_list_and_filter_payment_audit_events(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a payment audit event
  const now = new Date().toISOString();
  const base: IPaymentAudit.ICreate = {
    user_id: null, // system/automated event
    entity_type: "payment",
    entity_id: crypto.randomUUID(),
    action: "create",
    timestamp: now,
    old_value: null,
    new_value: JSON.stringify({ amount: 1000, status: "approved" }),
  };
  const created: IPaymentAudit = await api.functional.paymentsDiscounts.paymentAudits.post(connection, { body: base });
  typia.assert(created);
  TestValidator.equals("audit action")(base.action)(created.action);
  TestValidator.equals("entity id")(base.entity_id)(created.entity_id);

  // 2. List with no filter
  const page1: IPageIPaymentAudit = await api.functional.paymentsDiscounts.paymentAudits.patch(connection, { body: {} });
  typia.assert(page1);
  TestValidator.equals("at least one record exists")(true)(page1.data.length > 0);
  TestValidator.equals("pagination type")("number")(typeof page1.pagination.current);

  // 3. Search with specific filter (entity_type + entity_id)
  const page2: IPageIPaymentAudit = await api.functional.paymentsDiscounts.paymentAudits.patch(connection, {
    body: { entity_type: base.entity_type, entity_id: base.entity_id },
  });
  typia.assert(page2);
  TestValidator.equals("exact 1 match for freshly created entity")(
    1
  )(page2.data.length);
  TestValidator.equals("match ID")(created.id)(page2.data[0].id);

  // 4. Search with filter that yields nothing
  const page3: IPageIPaymentAudit = await api.functional.paymentsDiscounts.paymentAudits.patch(connection, {
    body: { action: "never-this-action" },
  });
  typia.assert(page3);
  TestValidator.equals("no matches on gibberish action")(0)(page3.data.length);

  // 5. Excessive page size & validate metadata
  const page4: IPageIPaymentAudit = await api.functional.paymentsDiscounts.paymentAudits.patch(connection, {
    body: { limit: 1000, page: 1 },
  });
  typia.assert(page4);
  TestValidator.equals("pagination limit respected")(1000)(page4.pagination.limit);
  TestValidator.equals("record count >=1")(true)(page4.pagination.records > 0);
}

