 *
 * 1. Create a payment audit entry using the POST endpoint to have a valid ID for retrieval.
 * 2. Retrieve the just-created payment audit entry by its ID and validate the response.
 * 3. Attempt to retrieve a payment audit entry with a random, non-existent ID and verify that 404 or appropriate error is returned.
 */
export async function test_api_payment_audit_retrieve_by_id_and_not_found(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a payment audit entry
  const auditCreate: IPaymentAudit.ICreate = {
    entity_type: "payment",
    entity_id: typia.random<string>(),
    action: "create",
    timestamp: new Date().toISOString(),
    user_id: null,
    old_value: null,
    new_value: JSON.stringify({ amount: 1500, status: "created" }),
  };
  const created: IPaymentAudit = await api.functional.paymentsDiscounts.paymentAudits.post(connection, {
    body: auditCreate,
  });
