import api from "@ORGANIZATION/PROJECT-api";
import { IPaymentAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IPaymentAudit";

/**
 * Validate the creation of a payment audit event, including success and failure scenarios.
 *
 * This test verifies that providing valid required fields to the payment audit event creation endpoint results in the audit event being properly persisted, and all audit fields are present and correct.
 * The test also checks for validation errors when required fields are missing or invalid data types are provided. This ensures the endpoint properly enforces compliance and auditing requirements.
 *
 * 1. Create a payment audit event using valid input data and confirm the response is accurate and complete.
 * 2. Attempt to create an audit event with missing required fields and expect a validation error.
 * 3. Attempt to create an audit event with invalid data types (e.g., string instead of uuid/date-time) and expect a validation error.
 */
export async function test_api_payment_audit_create_and_failure_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Success: Valid Payment Audit Creation
  const validPayload: IPaymentAudit.ICreate = {
    entity_type: "payment",
    entity_id: typia.random<string & typia.tags.Format<"uuid">>(),
    action: "create",
    timestamp: new Date().toISOString(),
    user_id: typia.random<string & typia.tags.Format<"uuid">>(),
    old_value: null,
    new_value: JSON.stringify({ status: "created" }),
  };
  const audit: IPaymentAudit = await api.functional.paymentsDiscounts.paymentAudits.post(connection, {
    body: validPayload,
  });
  typia.assert(audit);
  TestValidator.equals("entity_type")(validPayload.entity_type)(audit.entity_type);
  TestValidator.equals("entity_id")(validPayload.entity_id)(audit.entity_id);
  TestValidator.equals("action")(validPayload.action)(audit.action);
  TestValidator.equals("user_id")(validPayload.user_id)(audit.user_id);
  TestValidator.equals("old_value")(validPayload.old_value)(audit.old_value);
  TestValidator.equals("new_value")(validPayload.new_value)(audit.new_value);
  // Validate timestamp (ISO date)
  TestValidator.equals("timestamp")(validPayload.timestamp)(audit.timestamp);

  // 2. Failure: Missing required fields (omit entity_type)
  await TestValidator.httpError("missing required entity_type")(400)(() =>
    api.functional.paymentsDiscounts.paymentAudits.post(connection, {
      body: {
        entity_id: typia.random<string & typia.tags.Format<"uuid">>(),
        action: "create",
        timestamp: new Date().toISOString(),
        // entity_type is missing
      } as any, // force
    })
  );

  // 3. Failure: Invalid data type (timestamp as non-date string)
  await TestValidator.httpError("invalid timestamp format")(400)(() =>
    api.functional.paymentsDiscounts.paymentAudits.post(connection, {
      body: {
        entity_type: "payment",
        entity_id: typia.random<string & typia.tags.Format<"uuid">>(),
        action: "create",
        timestamp: "not-a-date-time",
      } as any,
    })
  );
}

