import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IPageIMessageThread } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIMessageThread";
import { IMessageThread } from "@ORGANIZATION/PROJECT-api/lib/structures/IMessageThread";

/**
 * Validate listing and searching of message threads with filters and pagination.
 *
 * Covers: creation of sample thread, generic listing, searching by subject,
 * pagination control, and no-result (graceful empty) queries.
 *
 * 1. Create a sample message thread with a unique subject to ensure that it can be found via filter.
 * 2. List message threads with no filters and confirm the created thread is present.
 * 3. Search message threads filtering by the specific subject used above and confirm at least one result matches, and all results match the subject filter.
 * 4. Use pagination (with limit=1) and verify that pagination metadata and data structure are correct.
 * 5. Query with a subject that does not exist and check that an empty list is returned but pagination metadata is still valid.
 */
export async function test_api_message_thread_list_and_filter_message_threads(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a sample message thread with unique subject
  const subject = `e2e-thread-` + Math.random().toString(36).slice(2);
  const created: IMessageThread = await api.functional.communicationReview.messageThreads.post(connection, {
    body: {
      subject,
      // created_by_user_id: omitted to use auth context
      archived: false,
    } satisfies IMessageThread.ICreate,
  });
  typia.assert(created);
  TestValidator.equals("thread subject")(subject)(created.subject);

  // 2. List message threads with no filters; expect the new thread among results
  const listAll: IPageIMessageThread = await api.functional.communicationReview.messageThreads.patch(connection, {
    body: {} satisfies IMessageThread.IRequest,
  });
  typia.assert(listAll);
  TestValidator.equals("pagination present")(true)(!!listAll.pagination);
  TestValidator.equals("data structure present")(true)(Array.isArray(listAll.data));
  TestValidator.equals("created thread listed")(
    true
  )(!!listAll.data.find((t) => t.id === created.id));

  // 3. Filter by subject
  const filtered: IPageIMessageThread = await api.functional.communicationReview.messageThreads.patch(connection, {
    body: { subject } satisfies IMessageThread.IRequest,
  });
  typia.assert(filtered);
  TestValidator.equals("at least one result")(true)(filtered.data.length > 0);
  filtered.data.forEach((t, idx) =>
    TestValidator.equals(`subject match #${idx}`)(subject)(t.subject),
  );

  // 4. Test pagination (limit=1)
  const paged: IPageIMessageThread = await api.functional.communicationReview.messageThreads.patch(connection, {
    body: { limit: 1, sort_field: "created_at", sort_order: "desc" } satisfies IMessageThread.IRequest,
  });
  typia.assert(paged);
  TestValidator.equals("limit=1")(1)(paged.pagination.limit);
  TestValidator.equals("page size")(1)(paged.data.length);

  // 5. Query with non-existent subject
  const noResult: IPageIMessageThread = await api.functional.communicationReview.messageThreads.patch(connection, {
    body: { subject: "non-existent-e2e-query-subject-" + Math.random().toString(36).slice(2) } satisfies IMessageThread.IRequest,
  });
  typia.assert(noResult);
  TestValidator.equals("empty data when no match")(0)(noResult.data.length);
  TestValidator.equals("pagination still present")(true)(!!noResult.pagination);
}

