import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IMessage } from "@ORGANIZATION/PROJECT-api/lib/structures/IMessage";
import { IPageIMessage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIMessage";

/**
 * Paginate and filter messages from the messages table by thread, sender, read/unread status, etc.
 * Validate limits, offsets, and empty results handling.
 *
 * Prerequisite: To perform list/query tests, at least one message must exist in the database. Therefore, you must first create a dummy message for a controlled thread and user, and use this for filter/pagination validation.
 *
 * Steps:
 * 1. Create a test message in a unique thread with a unique sender (call POST /communicationReview/messages)
 * 2. List messages filtered by the exact thread_id  result should include the created message
 * 3. List messages filtered by the sender_user_id  result should include the created message
 * 4. List messages filtered by a wrong thread_id  result should be an empty array
 * 5. List messages filtered by a wrong sender_user_id  result should be an empty array
 * 6. List messages with limit=1 and page=1 (pagination)  should succeed and contain at most 1 message
 * 7. List messages with high offset/page beyond last record  result should be empty
 * 8. List all messages (no filter)  result should include the created message
 * 9. Validate result type (pagination meta and data array), fields existence and correct types
 * 10. Validate data contains the created message in expected filters
 * 11. Confirm that unrelated filters (invalid id, etc.) return an empty result set
 */
export async function test_api_message_list_and_filter_messages(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a test message in a unique thread/user
  const createBody: IMessage.ICreate = {
    thread_id: typia.random<string & typia.tags.Format<"uuid">>(),
    content: "Test content for list & filter",
    message_type: "user",
    // Optionally parent_message_id
  };

  const created: IMessage = await api.functional.communicationReview.messages.post(connection, { body: createBody });
  typia.assert(created);

  // 2. List messages by thread_id
  const byThread: IPageIMessage = await api.functional.communicationReview.messages.patch(connection, {
    body: { thread_id: created.thread_id },
  });
  typia.assert(byThread);
  TestValidator.some("byThread includes created")(
    (msg) => msg.id === created.id
  )(byThread.data);

  // 3. List messages by sender_user_id
  const bySender: IPageIMessage = await api.functional.communicationReview.messages.patch(connection, {
    body: { sender_user_id: created.sender_user_id },
  });
  typia.assert(bySender);
  TestValidator.some("bySender includes created")(
    (msg) => msg.id === created.id
  )(bySender.data);

  // 4. List messages by wrong thread_id
  const wrongThread: IPageIMessage = await api.functional.communicationReview.messages.patch(connection, {
    body: { thread_id: typia.random<string & typia.tags.Format<"uuid">>() },
  });
  typia.assert(wrongThread);
  TestValidator.equals("empty on wrong thread_id")([])(wrongThread.data);

  // 5. List messages by wrong sender_user_id
  const wrongSender: IPageIMessage = await api.functional.communicationReview.messages.patch(connection, {
    body: { sender_user_id: typia.random<string & typia.tags.Format<"uuid">>() },
  });
  typia.assert(wrongSender);
  TestValidator.equals("empty on wrong sender_user_id")([])(wrongSender.data);

  // 6. List messages with limit=1, page=1
  const page1: IPageIMessage = await api.functional.communicationReview.messages.patch(connection, {
    body: { limit: 1, page: 1 },
  });
  typia.assert(page1);
  TestValidator.lessEqual("page1.data has at most 1")(1)(page1.data.length);

  // 7. List messages with high page (beyond records)
  const highPage: IPageIMessage = await api.functional.communicationReview.messages.patch(connection, {
    body: { limit: 1, page: 9999 },
  });
  typia.assert(highPage);
  TestValidator.equals("empty for high page")([])(highPage.data);

  // 8. List all messages (no filter)
  const unfiltered: IPageIMessage = await api.functional.communicationReview.messages.patch(connection, {
    body: {},
  });
  typia.assert(unfiltered);
  TestValidator.some("unfiltered includes created")(
    (msg) => msg.id === created.id
  )(unfiltered.data);

  // 9. Validate result type & fields
  for (const res of [byThread, bySender, wrongThread, wrongSender, page1, highPage, unfiltered]) {
    typia.assert(res);
    TestValidator.object("pagination meta")(["current", "limit", "records", "pages"])(res.pagination);
    TestValidator.array("data is array")(res.data);
    for (const m of res.data) {
      TestValidator.object("IMessage structure")([
        "id",
        "thread_id",
        "sender_user_id",
        "content",
        "message_type",
        "sent_at",
        "archived",
      ])(m);
    }
  }
}

import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IMessage } from "@ORGANIZATION/PROJECT-api/lib/structures/IMessage";

/**
 * Retrieve a message by valid ID, and confirm all standard content/metadata fields.
 * Also, ensure an error is properly returned when fetching with an invalid ID.
 *
 * 1. Create a message via POST /communicationReview/messages
 * 2. Retrieve the created message by its id with GET /communicationReview/messages/{id}
 * 3. Validate all fields (id, thread_id, sender_user_id, parent_message_id, content, message_type, sent_at, read_at, archived)
 * 4. Attempt to fetch by an invalid (random) id, confirm error response
 */
export async function test_api_message_get_by_id(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a message for retrieval
  const createInput: IMessage.ICreate = {
    thread_id: typia.random<string & typia.tags.Format<"uuid">>(),
    content: "Hello, world! This is a test.",
    message_type: "user",
    parent_message_id: null,
  };
  const created: IMessage = await api.functional.communicationReview.messages.post(connection, {
    body: createInput,
  });
  typia.assert(created);

  // 2. Retrieve the message by its valid id
  const fetched: IMessage = await api.functional.communicationReview.messages.getById(connection, {
    id: created.id,
  });
  typia.assert(fetched);

  // 3. Validate all fields match
  TestValidator.equals("id")(created.id)(fetched.id);
  TestValidator.equals("thread_id")(created.thread_id)(fetched.thread_id);
  TestValidator.equals("sender_user_id")(created.sender_user_id)(fetched.sender_user_id);
  TestValidator.equals("parent_message_id")(created.parent_message_id ?? null)(fetched.parent_message_id ?? null);
  TestValidator.equals("content")(created.content)(fetched.content);
  TestValidator.equals("message_type")(created.message_type)(fetched.message_type);
  TestValidator.equals("archived")(created.archived)(fetched.archived);
  // Timestamps can differ but should be present and in correct format
  typia.assert<typeof fetched.sent_at>(fetched.sent_at);

  // 4. Try getting a message by an invalid/random id, expect error
  await TestValidator.httpError("invalid id not found")(404)(() =>
    api.functional.communicationReview.messages.getById(connection, {
      id: typia.random<string & typia.tags.Format<"uuid">>(), // random UUID, likely not present
    })
  );
}

