import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { INotification } from "@ORGANIZATION/PROJECT-api/lib/structures/INotification";
import { IPageINotification } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageINotification";

/**
 * List and search notification records with pagination and filtering, including error and edge cases.
 *
 * This test ensures that notification searching via PATCH /communicationReview/notifications
 * works with various filters and pagination, and handles empty and out-of-range queries gracefully.
 *
 * 1. Create a notification so that test data exists for all queries (dependency)
 * 2. List notifications for the user without filters (expect at least 1)
 * 3. List notifications for the user by notification_type filter (match the created one)
 * 4. Check for only unread notifications
 * 5. Paginate with small limits (page 1, page 2)
 * 6. Query with non-existent user or type (should return empty)
 * 7. Query with out-of-range page number (should return empty/valid, not error)
 *
 * Assertion points:
 * - Total and filtered counts correct
 * - Pagination info is valid and consistent
 * - Notification fields match created data
 * - Empty results and edge cases handled (not crash)
 */
export async function test_api_notification_list_and_filter_notifications(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create notification
  const user_id = typia.random<string & typia.tags.Format<"uuid">>();
  const notification_type = `test-type-${Date.now()}`;
  const content = `test-content-${Math.random().toString(36).slice(2)}`;

  const created: INotification = await api.functional.communicationReview.notifications.post(connection, {
    body: {
      user_id,
      notification_type,
      content,
    },
  });
  typia.assert(created);
  TestValidator.equals("created user id")(user_id)(created.user_id);
  TestValidator.equals("created type")(notification_type)(created.notification_type);
  TestValidator.equals("created content")(content)(created.content);

  // 2. List notifications for user without filters
  const all: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
    body: { user_id },
  });
  typia.assert(all);
  TestValidator.equals("pagination user id")(user_id)(all.data[0].user_id);
  TestValidator.equals("at least one record")(true)(all.data.length >= 1);

  // 3. List notifications by notification_type
  const filtered: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
    body: { user_id, notification_type },
  });
  typia.assert(filtered);
  TestValidator.equals("filtered by type count")(1)(filtered.data.filter(n => n.notification_type === notification_type).length);
  TestValidator.equals("filtered by type user id")(user_id)(filtered.data[0]?.user_id);

  // 4. Check unread notifications (unread = read_at == null)
  const unread: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
    body: { user_id, archived: false },
  });
  typia.assert(unread);
  const unreadCount = unread.data.filter(n => n.read_at == null).length;
  TestValidator.equals("unread notifications include created")(true)(unread.data.some(n => n.id === created.id && n.read_at == null));

  // 5. Paginate with limit=1 (page 1, page 2)
  const page1: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
    body: { user_id, limit: 1, page: 1 },
  });
  typia.assert(page1);
  TestValidator.equals("page1 limit")(1)(page1.pagination.limit);
  TestValidator.equals("page1 current")(1)(page1.pagination.current);
  TestValidator.equals("page1 count")(page1.pagination.limit)(page1.data.length);

  const page2: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
    body: { user_id, limit: 1, page: 2 },
  });
  typia.assert(page2);
  TestValidator.equals("page2 current")(2)(page2.pagination.current);

  // 6. Query non-existent type or user (should be empty)
  const noType: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
    body: { user_id, notification_type: "__no_such_type__", limit: 10 },
  });
  typia.assert(noType);
  TestValidator.equals("no type returns empty")(0)(noType.data.length);

  const noUser: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
    body: { user_id: "00000000-0000-4000-8000-000000000000", limit: 10 },
  });
  typia.assert(noUser);
  TestValidator.equals("no user returns empty")(0)(noUser.data.length);

  // 7. Query out-of-range page (should not crash or error)
  const outOfRange: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
    body: { user_id, page: 99999, limit: 1 },
  });
  typia.assert(outOfRange);
  TestValidator.equals("out-of-range page empty")(0)(outOfRange.data.length);
}

