      TestValidator.equals("Filter verified_purchase")(true)(rev.verified_purchase);
    }
  }

  // 6. Pagination: limit = 1, page = 2
  {
    const paged: IPageIReview = await api.functional.communicationReview.reviews.patch(connection, {
      body: { limit: 1, page: 2 },
    });
    typia.assert(paged);
    TestValidator.equals("Pagination: page number")(2)(paged.pagination.current);
    TestValidator.equals("Pagination: limit")(1)(paged.pagination.limit);
    TestValidator.equals("Pagination: data length")(1)(paged.data.length);
  }

  // 7. Filters with no results (non-existent user/product)
  {
    const none: IPageIReview = await api.functional.communicationReview.reviews.patch(connection, {
      body: { user_id: "00000000-0000-0000-0000-000000000000" },
    });
    typia.assert(none);
    TestValidator.equals("Empty filter result")(0)(none.data.length);
    TestValidator.equals("Empty result pagination records")(0)(none.pagination.records);
  }

  // 8. Data consistency check: reordered reviews match creation
  {
    // using full detail check for reviewA
    const filtered: IPageIReview = await api.functional.communicationReview.reviews.patch(connection, {
      body: { user_id: reviewA.user_id, product_id: reviewA.product_id },
    });
