    TestValidator.equals("Data match")(reviewA.content)(filtered.data[0]?.content);
    TestValidator.equals("Data match: lang")(reviewA.language_code)(filtered.data[0]?.language_code);
    TestValidator.equals("Data match: verified_purchase")(reviewA.verified_purchase)(filtered.data[0]?.verified_purchase);
    TestValidator.equals("Data match: product_id")(reviewA.product_id)(filtered.data[0]?.product_id);
  }
}

