import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IReview } from "@ORGANIZATION/PROJECT-api/lib/structures/IReview";

/**
 * Validate update of an existing review by ID, covering valid and error scenarios.
 *
 * This E2E test verifies that a review can be updated as expected: fields such as content, language_code, and verified_purchase
 * are changed successfully and persisted. After the update, the test confirms changes by retrieving the updated review and
 * comparing relevant fields. The test also covers negative/error scenarios, ensuring proper errors are returned for updates with
 * non-existent or soft-deleted IDs.
 *
 * Detailed steps:
 * 1. Create a review for update testing (POST /communicationReview/reviews)
 * 2. Update the review by its ID, changing fields (PUT /communicationReview/reviews/{id})
 * 3. Retrieve the review and verify updated fields
 * 4. Attempt update with a non-existent review ID (should fail)
 * 5. Archive (soft-delete) the original review, then attempt update again (should fail)
 */
export async function test_api_review_update_by_id(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a review for update testing
  const reviewCreate: IReview = await api.functional.communicationReview.reviews.post(connection, {
    body: {
      user_id: typia.random<string>(),
      product_id: typia.random<string>(),
      content: "Initial content for e2e update test.",
      language_code: "en",
      verified_purchase: true
    } satisfies IReview.ICreate,
  });
  typia.assert(reviewCreate);

  // 2. Update fields (content, language_code, verified_purchase)
  const updateBody: IReview.IUpdate = {
    content: "UPDATED: This content was changed.",
    language_code: "fr",
    verified_purchase: false
  };
  const updated: IReview = await api.functional.communicationReview.reviews.putById(connection, {
    id: reviewCreate.id,
    body: updateBody,
  });
  typia.assert(updated);

  // 3. Retrieve again and verify relevant fields
  // (Assuming GET /communicationReview/reviews/{id} exists. If not, validate with response of update)
  TestValidator.equals("updated content")(updateBody.content)(updated.content);
  TestValidator.equals("updated language")(updateBody.language_code)(updated.language_code);
  TestValidator.equals("updated verified_purchase")(updateBody.verified_purchase)(updated.verified_purchase);

  // 4. Attempt update with non-existent ID
  await TestValidator.httpError("update non-existent review")(404)(() =>
    api.functional.communicationReview.reviews.putById(connection, {
      id: typia.random<string>(),
      body: updateBody,
    })
  );

  // 5. Archive (soft-delete) the review then attempt update
  const softDeleteBody: IReview.IUpdate = { archived: true };
  const archived: IReview = await api.functional.communicationReview.reviews.putById(connection, {
    id: reviewCreate.id,
    body: softDeleteBody,
  });
  typia.assert(archived);
  TestValidator.equals("archived flag after soft delete")(true)(archived.archived);

  await TestValidator.httpError("update soft-deleted review")(404)(() =>
    api.functional.communicationReview.reviews.putById(connection, {
      id: reviewCreate.id,
      body: updateBody,
    })
  );
}

