/**
 * List and search all review media records with various filters.
 *
 * This test validates the review media listing and filtering capabilities including pagination, empty result handling, and retrieval by specific fields.
 *
 * Scenario steps:
 * 1. Create at least one review media record to ensure there is something to query (prerequisite for all filtering/search tests).
 * 2. List all review media records with no filters (should return at least the newly created record).
 * 3. Filter review media by the associated review_id (should only reveal linked media).
 * 4. Filter by media_type ('image', 'video', etc; should only return items of that type).
 * 5. Filter by alt_text value (a11y, accessibility), should only match records with that alt-text.
 * 6. Test pagination; query with limit/page and verify output splits accordingly.
 * 7. Query with a filter that matches no entries (e.g., fake review_id) to ensure empty result handling.
 */
export async function test_api_review_media_list_and_filter(
  connection: api.IConnection
): Promise<void> {
  // 1. Create a review media record.
  const createInput: IReviewMedia.ICreate = {
    review_id: typia.random<string>(), // Replace with a valid review_id in full integration
    media_type: "image",
    uri: "https://example.com/media/test-image.jpg",
    alt_text: "Test alt text for accessibility",
    sequence: 1,
  };
  const created: IReviewMedia = await api.functional.communicationReview.reviewMedia.post(connection, { body: createInput });
  typia.assert(created);

  // 2. List all review media with no filters
  const allResult: IPageIReviewMedia = await api.functional.communicationReview.reviewMedia.patch(connection, { body: {} });
  typia.assert(allResult);
  TestValidator.equals("all media includes created")(true)(allResult.data.some(item => item.id === created.id));

  // 3. Filter by review_id
  const byReviewId: IPageIReviewMedia = await api.functional.communicationReview.reviewMedia.patch(connection, { body: { review_id: created.review_id } });
  typia.assert(byReviewId);
  TestValidator.equals("filter by review_id matches exactly 1")(1)(byReviewId.data.filter(item => item.id === created.id).length);

  // 4. Filter by media_type
  const byMediaType: IPageIReviewMedia = await api.functional.communicationReview.reviewMedia.patch(connection, { body: { media_type: created.media_type } });
  typia.assert(byMediaType);
  TestValidator.equals("media_type filter includes created")(true)(byMediaType.data.some(item => item.id === created.id));

  // 5. Filter by alt_text
  const byAltText: IPageIReviewMedia = await api.functional.communicationReview.reviewMedia.patch(connection, { body: { alt_text: created.alt_text ?? undefined } });
  typia.assert(byAltText);
  if (created.alt_text)
    TestValidator.equals("alt_text filter includes created")(true)(byAltText.data.some(item => item.id === created.id));

  // 6. Pagination - assuming multiple records exist
  const paged: IPageIReviewMedia = await api.functional.communicationReview.reviewMedia.patch(connection, { body: {} });
  typia.assert(paged);
  if (paged.pagination.records > 1) {
    // Query with limit 1, check page structure
    const paged1: IPageIReviewMedia = await api.functional.communicationReview.reviewMedia.patch(connection, { body: {} });
    typia.assert(paged1);
  }

  // 7. Ensure empty result case (use fake review_id)
  const fakeReviewId: string = "00000000-0000-0000-0000-000000000000";
  const emptyResult: IPageIReviewMedia = await api.functional.communicationReview.reviewMedia.patch(connection, { body: { review_id: fakeReviewId } });
  typia.assert(emptyResult);
  TestValidator.equals("empty filter")(0)(emptyResult.data.length);
}

