  const unauthorizedConnection = {
    ...connection,
    headers: {
      ...connection.headers,
      Authorization: undefined,
      authorization: undefined,
    },
  };
  await TestValidator.httpError("permission denied")(403)(() =>
    api.functional.core.users.patch(unauthorizedConnection, {
      body: {} satisfies IUser.IRequest,
    })
  );
}

