  TestValidator.equals("id unchanged")(user.id)(updatedUser.id);
  TestValidator.equals("created_at unchanged")(user.created_at)(updatedUser.created_at);

  // 3. Attempt to update with missing required fields (empty update object)
  await TestValidator.httpError("empty update object should fail")(400)(() =>
    api.functional.core.users.putById(connection, { id: user.id, body: {} }),
  );

