    api.functional.core.users.putById(connection, {
      id: fakeId,
      body: { username: "ghost" },
    }),
  );

  // 5. Attempt to update immutable fields (should have no effect or be blocked)
  await TestValidator.httpError("cannot update immutable fields")(400)(() =>
    api.functional.core.users.putById(connection, {
      id: user.id,
      body: { id: "some-other-id", created_at: new Date().toISOString() } as any,
    }),
  );
}

