import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IPageIUserProfile } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIUserProfile";
import { IUserProfile } from "@ORGANIZATION/PROJECT-api/lib/structures/IUserProfile";

/**
 * E2E test: User Profile List Pagination, Filtering, and Access Control.
 *
 * This test validates the paginated retrieval and filtering of user profiles from the core system.
 * Steps:
 * 1. Retrieve user profile list with default pagination, confirm metadata and data structure.
 * 2. Apply advanced filters (e.g., by locale_id), verify only matching records appear.
 * 3. Use an invalid filter (incorrect field type), expect validation error response.
 * 4. Attempt request as unauthorized user, verify access denied.
 *
 * This ensures compliance for access, pagination mechanics, audit requirements, and regulatory demands in user profile management scenarios.
 */
export async function test_api_user_profile_list_with_pagination_and_filtering(
  connection: api.IConnection,
): Promise<void> {
  // 1. Retrieve user profile list with default settings (no filters)
  const page1: IPageIUserProfile = await api.functional.core.userProfiles.patch(connection, {
    body: {}
  });
  typia.assert(page1);
  // Validate pagination meta fields are present and plausible
  TestValidator.equals("pagination current page")(1)(page1.pagination.current);
  TestValidator.equals("limit should be positive")(true)(page1.pagination.limit > 0);
  TestValidator.equals("pages should be >= 1")(true)(page1.pagination.pages >= 1);
  TestValidator.equals("records >= data.length")(true)(page1.pagination.records >= page1.data.length);

  // 2. Apply advanced filter by locale_id (if at least one exists)
  if (page1.data.length > 0 && page1.data[0].locale_id) {
    const filtered: IPageIUserProfile = await api.functional.core.userProfiles.patch(connection, {
      body: { locale_id: page1.data[0].locale_id }
    });
    typia.assert(filtered);
    for (const u of filtered.data) {
      TestValidator.equals("all users have the requested locale_id")(page1.data[0].locale_id)(u.locale_id);
    }
  }

  // 3. Use an invalid filter (bad UUID for locale_id) and expect error
  await TestValidator.httpError("invalid filter validation")(400)(() =>
    api.functional.core.userProfiles.patch(connection, {
      body: { locale_id: "not-a-uuid" as any },
    }),
  );

  // 4. Attempt as unauthorized actor (simulate by dropping auth header)
  const unauthConn = { ...connection, headers: { ...connection.headers, authorization: undefined } };
  await TestValidator.httpError("unauthorized access")(403)(() =>
    api.functional.core.userProfiles.patch(unauthConn, {
      body: {},
    }),
  );
}

