import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IUserProfile } from "@ORGANIZATION/PROJECT-api/lib/structures/IUserProfile";

/**
 * End-to-end test for user profile creation (full and error cases).
 *
 * This test covers:
 * 1. Creating a user required to associate with the profile.
 * 2. Creating a user profile by providing all required and optional deep metadata/localization fields and ensuring the returned record is valid and fully populated.
 * 3. Attempting to create a profile without required fields and checking that validation errors are returned.
 * 4. Attempting to create a profile for a user that already has one, verifying that a conflict or proper error handling occurs.
 */
export async function test_api_user_profile_create_full_and_error_cases(connection: api.IConnection): Promise<void> {
  // 1. Create a user
  const user: IUser = await api.functional.core.users.post(connection, {
    body: {
      username: `user_${Date.now()}`,
      email: `user_${Date.now()}@example.com`,
      password: "TestPassword1!",
      role_id: "11111111-1111-1111-1111-111111111111", // Use a known valid role ID or mock
      organization_id: "22222222-2222-2222-2222-222222222222", // Use valid org or omit if allowed
      phone: "+8201000000000",
    } satisfies IUser.ICreate
  });
  typia.assert(user);

  // 2. Create a user profile with full optional and required fields
  const profileInput: IUserProfile.ICreate = {
    user_id: user.id,
    locale_id: "33333333-3333-3333-3333-333333333333",
    full_name: "Test User",
    avatar_url: "https://cdn.example.com/avatar.png",
    timezone: "Asia/Seoul",
    bio: "This is a test user profile.",
    a11y_prefs: '{"contrast":"high","fontSize":"large"}',
  };
  const profile: IUserProfile = await api.functional.core.userProfiles.post(connection, { body: profileInput });
  typia.assert(profile);
  TestValidator.equals("user_id association")(user.id)(profile.user_id);
  TestValidator.equals("full_name")(profileInput.full_name)(profile.full_name);
  TestValidator.equals("locale_id")(profileInput.locale_id)(profile.locale_id);
  TestValidator.equals("timezone")(profileInput.timezone)(profile.timezone);

  // 3. Attempt to create a profile without required fields (missing user_id)
  await TestValidator.httpError("missing required field: user_id")(400)(() =>
    api.functional.core.userProfiles.post(connection, {
      body: {
        // user_id omitted
        locale_id: "33333333-3333-3333-3333-333333333333",
        full_name: "Incomplete User",
      } as any,
    })
  );

  // 4. Attempt to create a profile for the same user (should fail: conflict or proper error)
  await TestValidator.httpError("duplicate profile for same user")(409)(() =>
    api.functional.core.userProfiles.post(connection, {
      body: {
        user_id: user.id,
        full_name: "Another Profile For Same User",
      }
    })
  );
}

import { TestValidator } from "@nestia/e2e";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IUserProfile } from "@ORGANIZATION/PROJECT-api/lib/structures/IUserProfile";

/**
 * Validate updating an existing user profile with new localization preferences and metadata.
 * Also confirm audit trail, and handle edge cases (nonexistent profile, denial on insufficient permissions).
 *
 * 1. Create a user profile to be updated.
 * 2. Update the user profile's locale or metadata fields and confirm all changes (audit correctness).
 * 3. Attempt to update a profile with a random/nonexistent id and verify 404 or not-found is returned.
 * 4. Try updating a profile with insufficient or no permission and check access is denied.
 */
export async function test_api_user_profile_update_and_edge_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a user profile as prerequisite
  const createBody: IUserProfile.ICreate = {
    user_id: typia.random<string & tags.Format<"uuid">>(),
    locale_id: typia.random<string & tags.Format<"uuid">>(),
    full_name: "Update Test User",
    avatar_url: "https://cdn.example.com/avatar.png",
    timezone: "Asia/Seoul",
    bio: "Before update bio",
    a11y_prefs: "{}",
  };
  const baseProfile: IUserProfile = await api.functional.core.userProfiles.post(connection, {
    body: createBody,
  });
  typia.assert(baseProfile);

  // 2. Update profile fields (localization, metadata, a11y)
  const updateBody: IUserProfile.IUpdate = {
    locale_id: typia.random<string & tags.Format<"uuid">>(),
    full_name: "Updated Name",
    avatar_url: "https://cdn.example.com/new-avatar.png",
    timezone: "Europe/Paris",
    bio: "Updated bio",
    a11y_prefs: '{"fontSize": "large"}',
  };
  const updated: IUserProfile = await api.functional.core.userProfiles.putById(connection, {
    id: baseProfile.id,
    body: updateBody,
  });
  typia.assert(updated);
  // Confirm that profile has new updates
  TestValidator.equals("user_id unchanged")(baseProfile.user_id)(updated.user_id);
  TestValidator.equals("full_name updated")("Updated Name")(updated.full_name);
  TestValidator.equals("locale_id updated")(updateBody.locale_id)(updated.locale_id);
  TestValidator.equals("avatar_url updated")("https://cdn.example.com/new-avatar.png")(updated.avatar_url);
  TestValidator.equals("timezone updated")("Europe/Paris")(updated.timezone);
  TestValidator.equals("bio updated")("Updated bio")(updated.bio);
  TestValidator.equals("a11y_prefs updated")('{"fontSize": "large"}')(updated.a11y_prefs);
  // Check audit (updated_at changed)
  TestValidator.notEquals("updated_at timestamp changed")
    (baseProfile.updated_at)(updated.updated_at);

  // 3. Try to update a nonexistent profile and expect 404 error.
  await TestValidator.httpError("update unknown profile")(404)(() =>
    api.functional.core.userProfiles.putById(connection, {
      id: typia.random<string & tags.Format<"uuid">>(), // new random id not matching any created above
      body: updateBody,
    })
  );

  // 4. (Edge/Conceptual) Simulate updating a profile with insufficient permissions.
  // This would ideally switch to a connection/auth context with no right to edit this profile.
  // For E2E this could involve logging in as a different user or dropping auth headers,
  // but for this code, we simulate by calling with a dummy/empty connection (if supported) or annotate as expected error.
  // If permissions are enforced by auth/session, proper test infra would handle context, here we check if error occurs.
  // -- You may need to implement more sophisticated auth context switching as per test infra (-- TODO in real testbed).
  const fakeConnection = { ...connection, headers: { ...(connection.headers ?? {}), Authorization: "Bearer fake-token" } };
  await TestValidator.error("denied write on profile without permission")(() =>
    api.functional.core.userProfiles.putById(fakeConnection, {
      id: baseProfile.id,
      body: updateBody,
    })
  );
}

