import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IOrganization } from "@ORGANIZATION/PROJECT-api/lib/structures/IOrganization";
import { IPageIOrganization } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIOrganization";

/**
 * E2E Test: List and filter organizations with pagination and advanced filters.
 *
 * This test validates the core organization listing API. It aims to:
 * - Retrieve and verify a paginated, filterable list of organizations.
 * - Confirm that pagination (page/limit) works correctly.
 * - Test filter options (by name/code) and verify only matching organizations are returned.
 * - Evaluate error handling with invalid or edge-case parameters (e.g. negative page/limit).
 * - Validate that permission controls reject unauthorized access.
 *
 * Test Steps:
 * 1. List organizations with default pagination (no filters)
 * 2. List organizations filtered by name
 * 3. List organizations filtered by code
 * 4. List organizations with specific page and limit
 * 5. Attempt with invalid page/limit (e.g. negative/zero)
 * 6. Simulate call with insufficient permissions and expect rejection
 */
export async function test_api_organization_list_with_pagination_and_filters(
  connection: api.IConnection,
): Promise<void> {
  // 1. List organizations with default pagination
  const defaultOutput: IPageIOrganization = await api.functional.core.organizations.patch(connection, {
    body: {},
  });
  typia.assert(defaultOutput);
  TestValidator.equals("page step 1")(1)(defaultOutput.pagination.current);

  // Edge check: ensure data is array
  TestValidator.equals("array in data 1")(true)(Array.isArray(defaultOutput.data));

  // Pick first org for filter tests
  const org: IOrganization | undefined = defaultOutput.data[0];
  if (!org) throw new Error("No organizations found in default list.");

  // 2. List organizations filtered by name
  const byName: IPageIOrganization = await api.functional.core.organizations.patch(connection, {
    body: { name: org.name },
  });
  typia.assert(byName);
  for(const o of byName.data) TestValidator.equals("name filter")(org.name)(o.name);

  // 3. List organizations filtered by code
  const byCode: IPageIOrganization = await api.functional.core.organizations.patch(connection, {
    body: { code: org.code },
  });
  typia.assert(byCode);
  for(const o of byCode.data) TestValidator.equals("code filter")(org.code)(o.code);

  // 4. List orgs with specific pagination (e.g. page 2, limit 1)
  const paginated: IPageIOrganization = await api.functional.core.organizations.patch(connection, {
    body: { page: 2, limit: 1 },
  });
  typia.assert(paginated);
  TestValidator.equals("pagination step")(2)(paginated.pagination.current);
  TestValidator.equals("pagination limit")(1)(paginated.pagination.limit);

  // 5. Invalid page (negative)  expect error or empty
  await TestValidator.error("negative page error")(() =>
    api.functional.core.organizations.patch(connection, {
      body: { page: -1 },
    })
  );

  // 6. Insufficient permissions: simulate by removing credentials (if supported)
  const unauthConn = { ...connection, headers: {} };
  await TestValidator.error("auth required")(() =>
    api.functional.core.organizations.patch(unauthConn, {
      body: {},
    })
  );
}

