import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IOrganization } from "@ORGANIZATION/PROJECT-api/lib/structures/IOrganization";

/**
 * Validate organization creation and duplicate/validation handling.
 *
 * 1. Create an organization with all required fields (name/code), verify all return fields
 * 2. Attempt creation with missing "name" and expect error (validation failure)
 * 3. Attempt creation with missing "code" and expect error (validation failure)
 * 4. Attempt to create another organization with the same "name" (should fail - duplicate)
 * 5. Attempt to create another organization with the same "code" (should fail - duplicate)
 */
export async function test_api_organization_create_valid_and_duplicate_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create organization with all required fields
  const orgInput: IOrganization.ICreate = {
    name: `Test Org ${Date.now()}`,
    code: `test-org-${Date.now()}`,
  };
  const org: IOrganization = await api.functional.core.organizations.post(
    connection,
    { body: orgInput }
  );
  typia.assert(org);
  TestValidator.equals("name matches")(orgInput.name)(org.name);
  TestValidator.equals("code matches")(orgInput.code)(org.code);
  TestValidator.notUndefined("org.id")(org.id);
  TestValidator.notUndefined("org.created_at")(org.created_at);
  TestValidator.notUndefined("org.updated_at")(org.updated_at);

  // 2. Create org with missing name (should error)
  await TestValidator.httpError("missing name, should reject")(400)(() =>
    api.functional.core.organizations.post(
      connection,
      {
        body: {
          // @ts-expect-error
          code: `another-org-${Date.now()}`,
        },
      },
    ),
  );

  // 3. Create org with missing code (should error)
  await TestValidator.httpError("missing code, should reject")(400)(() =>
    api.functional.core.organizations.post(
      connection,
      {
        body: {
          // @ts-expect-error
          name: `OrgMissingCode ${Date.now()}`,
        },
      },
    ),
  );

  // 4. Duplicate organization name (should error)
  await TestValidator.httpError("duplicate name should fail")(409)(() =>
    api.functional.core.organizations.post(
      connection,
      {
        body: {
          name: orgInput.name,
          code: orgInput.code + "-dup2",
        },
      },
    ),
  );
  // 5. Duplicate organization code (should error)
  await TestValidator.httpError("duplicate code should fail")(409)(() =>
    api.functional.core.organizations.post(
      connection,
      {
        body: {
          name: orgInput.name + " Dup 3",
          code: orgInput.code,
        },
      },
    ),
  );
}

import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IOrganization } from "@ORGANIZATION/PROJECT-api/lib/structures/IOrganization";

/**
 * E2E test: update and error paths for organization.
 *
 * This test covers successful update of an organization's details, validates
 * the changes and audit chain, and exercises error paths including:
 *  - Updating a non-existent organization (expect 404 not found).
 *  - Attempting update without sufficient privileges (operation should be blocked).
 *
 * Steps:
 * 1. Create a new organization (prerequisite for update)
 * 2. Update organization name and/or code, verify changes
 * 3. Attempt to update a non-existent organization (expect 404)
 * 4. Attempt update as unauthorized user (expect error/permission denied)
 */
export async function test_api_organization_update_success_and_error_paths(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a new organization
  const created: IOrganization = await api.functional.core.organizations.post(
    connection,
    {
      body: {
        name: `Wrtn QA Org ${Date.now()}`,
        code: `wrtn-qa-${Date.now()}`,
      } satisfies IOrganization.ICreate,
    },
  );
  typia.assert(created);

  // 2. Update organization name and/or code
  const updatedFields = { name: created.name + " (updated)" } satisfies IOrganization.IUpdate;
  const updated: IOrganization = await api.functional.core.organizations.putById(
    connection,
    {
      id: created.id,
      body: updatedFields,
    },
  );
  typia.assert(updated);
  TestValidator.equals("Updated organization ID")(created.id)(updated.id);
  TestValidator.equals("Updated organization name")(updatedFields.name)(updated.name);

  // 3. Attempt to update a non-existent organization (expect 404)
  await TestValidator.httpError("Update non-existent organization")(404)(() =>
    api.functional.core.organizations.putById(connection, {
      id: "e68df59e-dc29-4f36-8d18-398a3b5f0000", // unlikely fixed UUID
      body: { name: "Ghost Org" } satisfies IOrganization.IUpdate,
    }),
  );

  // 4. Attempt update with insufficient privileges (simulate by removing auth header if possible)
  //   This block assumes the lack of required authentication will result in error (e.g., 401/403).
