import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IPageISession } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageISession";
import { ISession } from "@ORGANIZATION/PROJECT-api/lib/structures/ISession";

/**
 * Advanced session search E2E test: pagination, filtering, and error validation.
 *
 * - Performs session list search with multiple conditions (pagination, filtering by user, expiration, device, etc.).
 * - Checks for correctness of paged results and data structure.
 * - Tests with invalid pagination/filtering arguments to ensure errors occur as expected.
 * - Verifies that both active and expired/ended sessions are handled and distinguished in response.
 *
 * 1. Query sessions with basic pagination (page/limit), assert pagination metadata and list structure.
 * 2. Query with advanced filters (user_id, expiration date ranges, device/IP). Assert only filtered results are returned.
 * 3. Query for active (not revoked/expired) and expired/revoked sessions, checking separation in result.
 * 4. Attempt to search with invalid page/limit/filter arguments (page 0, negative values, malformatted dates, etc.), expect HTTP errors or type validation failure.
 *
 */
export async function test_api_session_advanced_search_with_multiple_conditions(
  connection: api.IConnection,
): Promise<void> {
  // 1. Query sessions with pagination
  const paged: IPageISession = await api.functional.core.sessions.patch(connection, {
    body: {
      page: 1,
      limit: 5,
    } satisfies ISession.IRequest,
  });
  typia.assert(paged);
  TestValidator.equals("pagination page/limit")(1)(paged.pagination.current);
  TestValidator.equals("pagination limit")(5)(paged.pagination.limit);

  // 2. Query with advanced filters (simulate: user_id, expires_before, expires_after, device_fingerprint, ip)
  const exampleSession = paged.data[0];
  if (exampleSession) {
    const filterByUser: IPageISession = await api.functional.core.sessions.patch(connection, {
      body: {
        user_id: exampleSession.user_id,
      } satisfies ISession.IRequest,
    });
    typia.assert(filterByUser);
    for (const s of filterByUser.data) {
      TestValidator.equals("all sessions belong to filtered user")(
        exampleSession.user_id,
      )(s.user_id);
    }

    if (exampleSession.device_fingerprint) {
      const filterByDevice: IPageISession = await api.functional.core.sessions.patch(connection, {
        body: {
          device_fingerprint: exampleSession.device_fingerprint,
        } satisfies ISession.IRequest,
      });
      typia.assert(filterByDevice);
      for (const s of filterByDevice.data) {
        TestValidator.equals("sessions by device match")(
          exampleSession.device_fingerprint,
        )(s.device_fingerprint);
      }
    }
    if (exampleSession.ip) {
      const filterByIp: IPageISession = await api.functional.core.sessions.patch(connection, {
        body: {
          ip: exampleSession.ip,
        } satisfies ISession.IRequest,
      });
      typia.assert(filterByIp);
      for (const s of filterByIp.data) {
        TestValidator.equals("sessions by ip match")(exampleSession.ip)(s.ip);
      }
    }
    // Expires before / after
    const filterExpires: IPageISession = await api.functional.core.sessions.patch(connection, {
      body: {
        expires_before: exampleSession.expires_at,
        expires_after: exampleSession.created_at,
      } satisfies ISession.IRequest,
    });
    typia.assert(filterExpires);
    for (const s of filterExpires.data) {
      TestValidator.equals("expires_before <= session.expires_at")(
        true
      )(s.expires_at <= exampleSession.expires_at);
      TestValidator.equals("expires_after >= session.created_at")(
        true
      )(s.expires_at >= exampleSession.created_at);
    }
  }

  // 3. Query for active (not revoked) vs expired/revoked sessions
  const allSessions: IPageISession = await api.functional.core.sessions.patch(connection, {
    body: {} satisfies ISession.IRequest,
  });
  typia.assert(allSessions);
  const actives = allSessions.data.filter((s) => !s.revoked_at && s.expires_at > new Date().toISOString());
  const expired = allSessions.data.filter((s) => !!s.revoked_at || s.expires_at <= new Date().toISOString());
  // Verifying active/expired existence (may both be empty depending on test DB)
  if (actives.length > 0)
    for (const s of actives) {
      TestValidator.equals("active session is not revoked/expired")(
        false
      )(!!s.revoked_at || s.expires_at <= new Date().toISOString());
    }
  if (expired.length > 0)
    for (const s of expired) {
      TestValidator.equals("expired/revoked session check")(true)(
        !!s.revoked_at || s.expires_at <= new Date().toISOString(),
      );
    }

  // 4. Error: invalid pagination/filters
  await TestValidator.httpError("page 0 causes error")(400)(() =>
    api.functional.core.sessions.patch(connection, {
      body: { page: 0 } satisfies ISession.IRequest,
    })
  );
  await TestValidator.httpError("negative page causes error")(400)(() =>
    api.functional.core.sessions.patch(connection, {
      body: { page: -1 } satisfies ISession.IRequest,
    })
  );
  await TestValidator.httpError("negative limit causes error")(400)(() =>
    api.functional.core.sessions.patch(connection, {
      body: { limit: -10 } satisfies ISession.IRequest,
    })
  );
  await TestValidator.httpError("malformed expires_before causes error")(400)(() =>
    api.functional.core.sessions.patch(connection, {
      body: { expires_before: "not-a-date" as any } satisfies ISession.IRequest,
    })
  );
}

