import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";

/**
 * Validate creation of a new user, covering normal and error scenarios.
 *
 * User records require username, email, password, and a valid role for onboarding; organization may be optional. This test ensures core flows and uniqueness, including normal, validation, and duplicate user creation.
 *
 * 1. Create a user with all required valid fields
 * 2. Attempt to create a user with missing mandatory fields (e.g., email)
 * 3. Attempt to create a user with invalid email format
 * 4. Attempt to create a duplicate user (same email/username)
 * 5. Confirm appropriate error codes for validation and conflicts
 */
export async function test_api_user_create_success_and_validation_cases(connection: api.IConnection): Promise<void> {
  // 1. Create a valid user
  const username = `user_${Date.now()}_${Math.random().toString().slice(2, 8)}`;
  const email = `unique_${Date.now()}_${Math.random().toString().slice(2, 8)}@mail.io`;
  const validUser: IUser.ICreate = {
    username,
    email,
    password: "securepass123!",
    role_id: "00000000-0000-0000-0000-000000000001", // Should match a valid role
    organization_id: undefined,
    phone: undefined,
  };
  const user: IUser = await api.functional.core.users.post(connection, { body: validUser });
  typia.assert(user);
  TestValidator.equals("username")(username)(user.username);
  TestValidator.equals("email")(email)(user.email);

  // 2. Fail user creation: missing email
  await TestValidator.httpError("missing email")(400)(() =>
    api.functional.core.users.post(connection, { body: { ...validUser, email: undefined } as any })
  );

  // 3. Fail user creation: invalid email format
  await TestValidator.httpError("invalid email")(400)(() =>
    api.functional.core.users.post(connection, { body: { ...validUser, email: "invalid_email" } as any })
  );

  // 4. Fail user creation: duplicate email/username
  await TestValidator.httpError("duplicate user")(409)(() =>
    api.functional.core.users.post(connection, { body: validUser })
  );
}

import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { ISession } from "@ORGANIZATION/PROJECT-api/lib/structures/ISession";

/**
 * Validate session update logic and error handling.
 *
 * This scenario covers updating an existing session's properties, verifying that mutation persists, and testing error management for both nonexistent session IDs and permission failures.
 *
 * 1. Create a new session using normal user context.
 * 2. Update the session's properties (e.g., mark as revoked or change expiry).
 * 3. Fetch the session and validate that changes have persisted.
 * 4. Attempt to update a session using an invalid/nonexistent ID and ensure 404 Not Found is returned.
 * 5. Attempt to update session attributes while lacking appropriate permissions (simulate unauthorized actor) and ensure access is denied.
 */
export async function test_api_session_update_success_and_error_handling(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a new session
  const createInput: ISession.ICreate = {
    user_id: typia.random<string>(),
    token: "test-token",
    expires_at: new Date(Date.now() + 60 * 60 * 1000).toISOString(),
    device_fingerprint: "device-1234",
    ip: "127.0.0.1",
  };
  const session: ISession = await api.functional.core.sessions.post(connection, { body: createInput });
  typia.assert(session);

  // 2. Update the session properties (e.g., set revoked_at)
  const updateInput: ISession.IUpdate = {
    revoked_at: new Date().toISOString(),
    expires_at: new Date(Date.now() + 2 * 60 * 60 * 1000).toISOString(),
    device_fingerprint: "device-5678",
  };
  const updated: ISession = await api.functional.core.sessions.putById(connection, {
    id: session.id,
    body: updateInput,
  });
  typia.assert(updated);
  TestValidator.equals("session revoked_at change")(updateInput.revoked_at)(updated.revoked_at);
  TestValidator.equals("device fingerprint update")(updateInput.device_fingerprint)(updated.device_fingerprint);

  // 3. Re-fetch the session and verify changes
  // (Assumes there is a 'getById' or similar endpoint; if not, validate the updated return above)

  // 4. Attempt to update a session that does not exist (expect 404)
  await TestValidator.httpError("update nonexistent session")(404)(() =>
    api.functional.core.sessions.putById(connection, {
      id: typia.random<string>(), // Not present in DB
      body: updateInput,
    })
  );

  // 5. Attempt to update with insufficient permissions (simulate non-owner/non-admin)
  // (Assumes "connection" can be instantiated to simulate another user or unauthenticated context)
  if (connection.simulate) {
    const unauthorizedConn = { ...connection, headers: { ...connection.headers, Authorization: "Bearer invalid-token" } };
    await TestValidator.httpError("update forbidden")(403)(() =>
      api.functional.core.sessions.putById(unauthorizedConn as api.IConnection, {
        id: session.id,
        body: updateInput,
      })
    );
  }
}

import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { ICoreSession } from "@ORGANIZATION/PROJECT-api/lib/structures/ICoreSession";
import { ISession } from "@ORGANIZATION/PROJECT-api/lib/structures/ISession";

/**
 * Validate session deletion (soft-delete/revoke), handle non-existent session deletion error, and insufficient permissions error.
 *
 * Scenario:
 * 1. Create a user session (simulate login/session creation process).
 * 2. Delete (revoke) the created session by ID.
 * 3. Validate that session record is reported deleted (success = true, matching id, deleted_at).
 * 4. Try deleting a non-existent session ID (expect error).
 * 5. Attempt to delete another user's session or while lacking sufficient permissions (expect error).
 */
export async function test_api_session_delete_soft_and_permission(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a user session
  const session: ISession = await api.functional.core.sessions.post(connection, {
    body: {
      user_id: typia.random<string>(),
      token: `test-delete-session-${Date.now()}`,
      expires_at: new Date(Date.now() + 86_400_000).toISOString(), // +1 day
      device_fingerprint: null,
      ip: null,
    } satisfies ISession.ICreate,
  });
  typia.assert(session);

  // 2. Delete (revoke) the created session
  const deleted: ICoreSession.IDeleteResult = await api.functional.core.sessions.eraseById(connection, { id: session.id });
  typia.assert(deleted);
  TestValidator.equals("deleted.session_id")(session.id)(deleted.session_id);
  TestValidator.equals("deleted.success")(true)(deleted.success);
  TestValidator.type("deleted_at is ISO date")("string")(typeof deleted.deleted_at);

  // 3. Attempt to delete a non-existent session ID (expect error)
  await TestValidator.httpError("delete non-existent session")(404)(() =>
    api.functional.core.sessions.eraseById(connection, {
      id: typia.random<string>(),
    })
  );

  // 4. (If API supports permissions) Simulate insufficient permission: try to delete another session while not authorized.
  //    The details of impersonation or auth token swap would be app-specific and may require manual session or role switch here.
  //    This is sketched as an example of permission error validation.

  // Simulate lack of permission by using an unauthorized connection or purposely invalid session for API call, if possible.
  // Here we'll pass an obviously invalid connection header to simulate this.
  const unauthorizedConnection = { ...connection, headers: { ...connection.headers, Authorization: "Bearer invalid-or-missing" } };
  await TestValidator.httpError("delete session without permission")(403)(() =>
    api.functional.core.sessions.eraseById(unauthorizedConnection, {
      id: session.id,
    })
  );
}

import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { ICoreLocale } from "@ORGANIZATION/PROJECT-api/lib/structures/ICoreLocale";
import { IPageICoreLocale } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICoreLocale";

/**
 * Validate retrieving a paginated and filterable list of locales (languages/regions).
 *
 * This function tests the ability to list locales with pagination, search, and filtering by language code or name.
 * It also covers error cases such as invalid parameters and insufficient privileges (permission errors).
 *
 * 1. Retrieve locale list paginated (default parameters)
 * 2. Retrieve locales with fuzzy search (e.g., search for "en")
 * 3. Retrieve locales with explicit filter by code (e.g., filter: { code: "en-US" })
 * 4. Use pagination options (e.g., page, limit) and verify responses
 * 5. Attempt retrieval with invalid parameters (e.g., negative page/limit, nonsense filter) to trigger error validation
 * 6. Attempt to access the endpoint as a user lacking required permissions to ensure proper 403/401 error responses
 */
export async function test_api_core_locale_list_with_filters_and_permissions(
  connection: api.IConnection,
): Promise<void> {
  // 1. Retrieve locale list with default pagination
  const defaultList: IPageICoreLocale = await api.functional.core.locales.patch(
    connection,
    { body: {} satisfies ICoreLocale.IRequest },
  );
  typia.assert(defaultList);

  // 2. Retrieve with fuzzy search on code/name
  const searchEn: IPageICoreLocale = await api.functional.core.locales.patch(
    connection,
    { body: { search: "en" } satisfies ICoreLocale.IRequest },
  );
  typia.assert(searchEn);
  // At least one result should have code or name containing 'en'
  TestValidator.equals("at least one locale contains 'en'")(
    true
  )(
    searchEn.data.some(
      (l) => l.code.includes("en") || l.name.toLowerCase().includes("en")
    )
  );

  // 3. Explicit filter by known code if possible
  if (defaultList.data.length > 0) {
    const sampleLocale = defaultList.data[0];
    const byCode: IPageICoreLocale = await api.functional.core.locales.patch(
      connection,
      { body: { filter: { code: sampleLocale.code } } satisfies ICoreLocale.IRequest },
    );
    typia.assert(byCode);
    TestValidator.equals("exact code filter")(
      true
    )(
      byCode.data.every((l) => l.code === sampleLocale.code)
    );
  }

  // 4. Pagination: request page 1, limit 2 (if possible)
  const paged: IPageICoreLocale = await api.functional.core.locales.patch(
    connection,
    { body: { page: 1, limit: 2 } satisfies ICoreLocale.IRequest },
  );
  typia.assert(paged);
  TestValidator.equals("paged.limit")(
    2
  )(
    paged.pagination.limit
  );

  // 5. Error path: invalid parameters (e.g., negative page)
  await TestValidator.httpError("negative page")(
    400
  )(() =>
    api.functional.core.locales.patch(connection, {
      body: { page: -1 } satisfies ICoreLocale.IRequest,
    })
  );

  // 6. Permission error (simulate lack of privileges)
  // Assumes test infra allows modifying connection to drop access rights
  const unauthConnection = {
    ...connection,
    headers: { ...connection.headers, authorization: "Bearer INVALID-TOKEN" },
  };
  await TestValidator.httpError("unauthorized access")(
    401
  )(() =>
    api.functional.core.locales.patch(unauthConnection, {
      body: {} satisfies ICoreLocale.IRequest,
    })
  );
}

import { TestValidator } from "@nestia/e2e";
