 *
 * This scenario validates the admin-facing locale update flow and its key error controls:
 *
 * 1. Create a locale entry via POST /core/locales to update.
 * 2. Update the created locale entry with new code and/or name via PUT /core/locales/{id}. Verify the update succeeds and audit trace is recorded (if auditable field available; otherwise comment).
 * 3. Attempt to update a non-existent locale (random uuid) via PUT /core/locales/{id} and verify error is handled (404 expected).
 * 4. Attempt to perform update without proper admin privileges (simulate by removing/invalidating credentials if possible) and verify the API returns a permission-denied error (e.g., 403 or 401).
 */
export async function test_api_locale_update_handle_invalid_and_permission(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a locale entry
  const original: ICoreLocale = await api.functional.core.locales.post(
    connection,
    {
      body: {
        code: `e2e-${Date.now()}`,
        name: `E2E Test Locale ${Date.now()}`,
      } satisfies ICoreLocale.ICreate,
    },
  );
  typia.assert(original);

  // 2. Update the created locale entry
  const updated: ICoreLocale = await api.functional.core.locales.putById(
    connection,
    {
      id: original.id,
      body: {
        code: `${original.code}-mod`,
        name: `${original.name} (Modified)`,
      } satisfies ICoreLocale.IUpdate,
    },
  );
  typia.assert(updated);
  TestValidator.equals("locale id should stay the same")(original.id)(updated.id);
  TestValidator.equals("locale code should update")(`${original.code}-mod`)(updated.code);
  TestValidator.equals("locale name should update")(`${original.name} (Modified)`)(updated.name);
  // NOTE: If audit log/trace is exposed in response, check it. Otherwise, ensure no error thrown.

  // 3. Attempt update of a non-existent locale (404)
  const nonExistentId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("updating missing locale should 404")(404)(() =>
    api.functional.core.locales.putById(connection, {
      id: nonExistentId,
      body: { code: "xx-test", name: "Should Not Exist" } satisfies ICoreLocale.IUpdate,
    }),
  );

  // 4. Attempt update without sufficient privilege (401/403)
  const unauthenticated = { ...connection, headers: {} };
  await TestValidator.error("permission denied for unauthenticated user")(() =>
    api.functional.core.locales.putById(unauthenticated, {
      id: original.id,
      body: { code: "hack-attempt", name: "X" } satisfies ICoreLocale.IUpdate,
    }),
  );
}

