import api from "@ORGANIZATION/PROJECT-api";
import { ICatalogEvidence } from "@ORGANIZATION/PROJECT-api/lib/structures/ICatalogEvidence";
import { IPageICatalogEvidence } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICatalogEvidence";

/**
 * Test retrieving a paginated list of catalog evidence records with filters applied.
 *
 * This scenario verifies the /productCatalog/catalogEvidence PATCH endpoint for evidence listing with pagination and filter support.
 * It checks the response for proper pagination metadata as well as evidence data array,
 * including edge cases: no matching evidence (empty result), large dataset, and invalid filter parameters (error/empty/validation check).
 *
 * Steps:
 * 1. Request evidence list with no filters (all evidence, default pagination)
 * 2. Request first page with a low limit (simulate paging)
 * 3. Request with an unlikely filter (expect empty result)
 * 4. Request with known invalid parameters (expect error or empty)
 * 5. If possible, validate pagination metadata for page, limit, records, and pages fields in response
 */
export async function test_api_catalog_evidence_list_with_pagination_and_filters(
  connection: api.IConnection,
): Promise<void> {
  // 1. Request all evidence without filters (default paging)
  const allEvidence: IPageICatalogEvidence = await api.functional.productCatalog.catalogEvidence.patch(connection, {
    body: {} satisfies ICatalogEvidence.IRequest,
  });
  typia.assert(allEvidence);
  TestValidator.equals("array or empty data")(true)(Array.isArray(allEvidence.data));

  // 2. Request evidence with pagination (limit 2 per page)
  const pagedEvidence: IPageICatalogEvidence = await api.functional.productCatalog.catalogEvidence.patch(connection, {
    body: {
      page: 1,
      limit: 2,
    } satisfies ICatalogEvidence.IRequest,
  });
  typia.assert(pagedEvidence);
  TestValidator.equals("pagination limit")(2)(pagedEvidence.pagination?.limit || 2);
  TestValidator.equals("pagination current")(1)(pagedEvidence.pagination?.current || 1);

  // 3. Request with filter that should result in 0 evidence
  const emptyEvidence: IPageICatalogEvidence = await api.functional.productCatalog.catalogEvidence.patch(connection, {
    body: {
      description: "TotallyUnlikelyEvidenceDescriptionThatShouldNotExist",
    } satisfies ICatalogEvidence.IRequest,
  });
  typia.assert(emptyEvidence);
  TestValidator.equals("empty data for unlikely filter")(0)(emptyEvidence.data?.length || 0);

  // 4. Invalid parameter test (wrong date format)
  await TestValidator.error("invalid created_at_from format should fail or return empty")(() =>
    api.functional.productCatalog.catalogEvidence.patch(connection, {
      body: {
        created_at_from: "not-a-date-time-format",
      } satisfies ICatalogEvidence.IRequest,
    }),
  );

  // 5. Large page limit for large dataset (if any exists)
  const bigEvidence: IPageICatalogEvidence = await api.functional.productCatalog.catalogEvidence.patch(connection, {
    body: {
      page: 1,
      limit: 100,
    } satisfies ICatalogEvidence.IRequest,
  });
