    } satisfies ICart.ICreate,
  });
  typia.assert(cart);

  // 2. Create a cart item with valid product_variant_id
  const product_variant_id = typia.random<string & tags.Format<"uuid">>();
  const cartItem: ICartOrderCartItem = await api.functional.cartOrder.cartItem.post(connection, {
    body: {
      cart_id: cart.id,
      product_variant_id,
      quantity: 2,
    } satisfies ICartOrderCartItem.ICreate,
  });
  typia.assert(cartItem);

  // 3. Update the cart item with a new valid quantity
  const updatedQuantity = 5;
  const updatedCartItem: ICartOrderCartItem = await api.functional.cartOrder.cartItem.putById(connection, {
    id: cartItem.id,
    body: {
      quantity: updatedQuantity,
    } satisfies ICartOrderCartItem.IUpdate,
  });
  typia.assert(updatedCartItem);
  TestValidator.equals("quantity updated")(updatedQuantity)(updatedCartItem.quantity);

  // 4. Attempt to update with negative quantity
  await TestValidator.httpError("negative quantity should fail")(400)(() =>
    api.functional.cartOrder.cartItem.putById(connection, {
      id: cartItem.id,
      body: {
        quantity: -1,
      } satisfies ICartOrderCartItem.IUpdate,
    }),
  );

  // 5. Attempt to update with zero quantity
  await TestValidator.httpError("zero quantity should fail")(400)(() =>
    api.functional.cartOrder.cartItem.putById(connection, {
      id: cartItem.id,
      body: {
        quantity: 0,
      } satisfies ICartOrderCartItem.IUpdate,
    }),
  );

  // 6. Attempt to update a non-existent cart item
  await TestValidator.httpError("non-existent cart item")(404)(() =>
    api.functional.cartOrder.cartItem.putById(connection, {
      id: typia.random<string & tags.Format<"uuid">>(),
      body: {
        quantity: 1,
      } satisfies ICartOrderCartItem.IUpdate,
    }),
  );
}

/**
 * E2E tests for cart item deletion and related permission scenarios.
 *
 * This suite covers logical deletion, evidence compliance, and cross-user permission enforcement.
 *
 * 1. Create a new cart for a user (UserA).
 * 2. Add an item to the user's cart.
 * 3. Delete the item by valid ID and confirm audit evidence.
 * 4. Try to delete the same item again (should fail with 404 or similar error)
 * 5. Attempt deletion with a random invalid UUID (should fail with 404 error).
