export async function test_api_cartOrder_cartItem_delete_and_permission_scenarios(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a new cart for UserA
  const userAId = typia.random<string & typia.tags.Format<"uuid">>();
  const cartA: ICart = await api.functional.cartOrder.cart.post(connection, {
    body: { user_id: userAId } satisfies ICart.ICreate,
