    body: { cart_id: cartA.id, product_variant_id: productVariantA, quantity: 2 } satisfies ICartOrderCartItem.ICreate,
  });
  typia.assert(cartItemA);

  // 3. Delete the cart item by valid ID
  const deleted: ICartOrderCartItemDeleteResult = await api.functional.cartOrder.cartItem.eraseById(connection, { id: cartItemA.id });
  typia.assert(deleted);
  TestValidator.equals("deleted cart item id matches")(cartItemA.id)(deleted.id);

  // 4. Try to delete the same item again (should fail)
  await TestValidator.httpError("deleting already deleted cart item should fail")(404)(() =>
    api.functional.cartOrder.cartItem.eraseById(connection, { id: cartItemA.id }),
  );

  // 5. Attempt deletion with a random invalid UUID
  const invalidUUID = typia.random<string & typia.tags.Format<"uuid">>();
  await TestValidator.httpError("deleting non-existent cart item should fail")(404)(() =>
    api.functional.cartOrder.cartItem.eraseById(connection, { id: invalidUUID }),
  );

  // 6. Create a second user (UserB), their own cart, and item
  const userBId = typia.random<string & typia.tags.Format<"uuid">>();
  const cartB: ICart = await api.functional.cartOrder.cart.post(connection, {
    body: { user_id: userBId } satisfies ICart.ICreate,
  });
