  TestValidator.equals("pagination meta present")(
    ["current", "limit", "records", "pages"]
  )(
    Object.keys(itemsByOrder.pagination).sort(),
  );

  // 3. If items exist, filter by product_variant_id
  if (itemsByOrder.data.length > 0) {
    const productVariantId = itemsByOrder.data[0].product_variant_id;
    const itemsByProduct: IPageICartOrderOrderItem = await api.functional.cartOrder.orderItem.patch(
      connection,
      {
        body: {
          product_variant_id: productVariantId,
        } satisfies ICartOrderOrderItem.IRequest,
      },
    );
    typia.assert(itemsByProduct);
    TestValidator.equals("product variant filter works")(
      true,
    )(
      itemsByProduct.data.every((item) => item.product_variant_id === productVariantId),
    );
  }

  // 4. Pagination check (limit results to 1)
  const paged: IPageICartOrderOrderItem = await api.functional.cartOrder.orderItem.patch(
    connection,
    {
      body: {
        order_id: order.id,
      } satisfies ICartOrderOrderItem.IRequest,
    },
  );
  typia.assert(paged);
  TestValidator.equals("pagination count matches meta")(
    paged.data.length <= paged.pagination.limit,
  )(true);

  // 5. Date range check (should include the order item if time range is large)
  if (itemsByOrder.data.length > 0) {
    const addedAt = itemsByOrder.data[0].added_at;
    // search with a time window that must include this item
    const itemsInDate: IPageICartOrderOrderItem = await api.functional.cartOrder.orderItem.patch(
      connection,
      {
        body: {
          added_at_range: ["2000-01-01T00:00:00Z", "2100-01-01T00:00:00Z"],
        } satisfies ICartOrderOrderItem.IRequest,
      },
    );
    typia.assert(itemsInDate);
    TestValidator.equals("date range includes at least one item")(
      true,
    )(
      itemsInDate.data.length > 0,
    );
  }

  // 6. Invalid/empty filter should return empty result
  const emptyResult: IPageICartOrderOrderItem = await api.functional.cartOrder.orderItem.patch(
    connection,
    {
      body: {
        order_id: "55555555-5555-5555-5555-555555555555",
      } satisfies ICartOrderOrderItem.IRequest,
    },
  );
  typia.assert(emptyResult);
  TestValidator.equals("invalid order id returns zero")(
    0,
  )(emptyResult.data.length);
  // 7. Simulate unauthorized access (not implemented here, would require another connection)
  // This would depend on fixture for admin/user session management
  // You would write a second test-context with a new connection as another user and try to read this order's items, expecting forbidden or empty result
}

