 * 7. Validate pagination object and evidence auditability.
 * 8. Check the record chains so all status transitions in system remain traceable.
 */
export async function test_api_order_status_list_with_filters_pagination_and_auditability(
  connection: api.IConnection,
): Promise<void> {
  // 1. Query order_status with no filters
  const allResult: IPageIOrderStatus = await api.functional.cartOrder.orderStatus.patch(connection, {
    body: {},
  });
  typia.assert(allResult);
  TestValidator.equals("paginated data present")(true)(Array.isArray(allResult.data));
  TestValidator.equals("page info present")(true)(typeof allResult.pagination === "object");

  // 2. If results exist, pick a real order_id/status_code/timestamp from data
  let exampleOrderId: string | undefined = undefined;
  let exampleStatusCode: string | undefined = undefined;
  let exampleChangedAt: string | undefined = undefined;
  if (allResult.data.length > 0) {
    exampleOrderId = allResult.data[0].order_id;
    exampleStatusCode = allResult.data[0].status_code;
    exampleChangedAt = allResult.data[0].changed_at;
  }

  // 3. Filter by order_id (if we have a real one)
  if (exampleOrderId) {
    const byOrderId = await api.functional.cartOrder.orderStatus.patch(connection, {
      body: { order_id: exampleOrderId },
    });
    typia.assert(byOrderId);
    for (const record of byOrderId.data) {
      TestValidator.equals("correct order id")(
        exampleOrderId
      )(record.order_id);
    }
  }

  // 4. Filter by status_code
  if (exampleStatusCode) {
    const byStatusCode = await api.functional.cartOrder.orderStatus.patch(connection, {
      body: { status_code: exampleStatusCode },
    });
    typia.assert(byStatusCode);
    for (const record of byStatusCode.data) {
      TestValidator.equals("correct status code")(
        exampleStatusCode
      )(record.status_code);
    }
  }

  // 5. Filter by changed_at_range (if we have a timestamp)
  if (exampleChangedAt) {
    // Make a range [t, t] -- should yield at least the chosen event if allowed
    const ts = exampleChangedAt;
    const byTime = await api.functional.cartOrder.orderStatus.patch(connection, {
      body: { changed_at_range: [ts, ts] },
    });
    typia.assert(byTime);
    for (const record of byTime.data) {
      TestValidator.equals("changed_at matches")(ts)(record.changed_at);
    }
  }

  // 6. Combine filters for intersection (order + status + timestamp)
  if (exampleOrderId && exampleStatusCode && exampleChangedAt) {
    const combo = await api.functional.cartOrder.orderStatus.patch(connection, {
      body: {
        order_id: exampleOrderId,
        status_code: exampleStatusCode,
        changed_at_range: [exampleChangedAt, exampleChangedAt],
      },
    });
    typia.assert(combo);
    for (const record of combo.data) {
      TestValidator.equals("combo filter order_id")(exampleOrderId)(record.order_id);
      TestValidator.equals("combo filter status_code")(exampleStatusCode)(record.status_code);
      TestValidator.equals("combo filter changed_at")(exampleChangedAt)(record.changed_at);
    }
  }

  // 7. Try filter that yields no results (random UUID)
  const uuid = "00000000-0000-0000-0000-000000000000";
  const emptyResult = await api.functional.cartOrder.orderStatus.patch(connection, {
    body: { order_id: uuid },
  });
  typia.assert(emptyResult);
  TestValidator.equals("no result for fake order_id")(0)(emptyResult.data.length);

  // 8. Verify auditability: each order should have traceable status change history (if data exists)
  for (const record of allResult.data) {
    TestValidator.equals("id present")("string")(typeof record.id);
    TestValidator.equals("order_id present")("string")(typeof record.order_id);
    TestValidator.equals("status_code present")("string")(typeof record.status_code);
    TestValidator.equals("changed_at present")("string")(typeof record.changed_at);
  }
}

