/**
 * Validate updating of payment methods (non-sensitive and error scenarios).
 *
 * This test confirms that only allowed fields of a payment method can be updated, and restricted/sensitive fields do not change.
 * Error cases (non-existing ID) are also tested.
 *
 * 1. Create a payment method (precondition)
 * 2. Update allowed non-sensitive fields (masked_detail, provider, active) and verify the changes are reflected
 * 3. Attempt to update restricted/sensitive fields (e.g., user_id, type) and ensure they're not updated (remain unchanged)
 * 4. Attempt to update with an invalid (non-existing) ID and expect HTTP 404 error
 */
export async function test_api_payment_method_update(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a payment method as prerequisite
  const createBody: IPaymentMethod.ICreate = {
    user_id: typia.random<string>(),
    type: "VISA",
    masked_detail: "****-****-****-1234",
    provider: "Stripe",
    active: true,
  };
  const method: IPaymentMethod = await api.functional.paymentsDiscounts.paymentMethods.post(connection, { body: createBody });
  typia.assert(method);

  // 2. Update allowed fields
  const updateBody: IPaymentMethod.IUpdate = {
    masked_detail: "****-4321",
    provider: "Toss",
    active: false,
  };
  const updated: IPaymentMethod = await api.functional.paymentsDiscounts.paymentMethods.putById(connection, {
    id: method.id,
    body: updateBody,
  });
  typia.assert(updated);
  TestValidator.equals("Masked detail updated")(updateBody.masked_detail)(updated.masked_detail);
  TestValidator.equals("Provider updated")(updateBody.provider)(updated.provider);
  TestValidator.equals("Active updated")(updateBody.active)(updated.active);

  // 3. Attempt to update restricted/sensitive fields (user_id, type)
  const rejectUpdateBody: any = { user_id: typia.random<string>(), type: "Wallet" };
  await TestValidator.httpError("Update restricted fields")(400)(() =>
    api.functional.paymentsDiscounts.paymentMethods.putById(connection, {
      id: method.id,
      body: rejectUpdateBody,
    })
  );

  // 4. Attempt to update a non-existent payment method ID
  await TestValidator.httpError("Non-existent payment method ID")(404)(() =>
    api.functional.paymentsDiscounts.paymentMethods.putById(connection, {
      id: typia.random<string>(),
      body: updateBody,
    })
  );
}

