import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IDiscount } from "@ORGANIZATION/PROJECT-api/lib/structures/IDiscount";
import { IPageIDiscount } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIDiscount";

/**
 * Retrieve a filtered, paginated list of discount records.
 *
 * This test validates the discount search API by:
 * 1. Creating multiple discounts, each with unique discount_rule_id, order_id, amount, and notes.
 * 2. Searching by various fields including discount_rule_id, order_id, amount, and notes.
 * 3. Testing pagination (limit and page) and ensuring paged data is consistent.
 * 4. Verifying that filters yielding no results return empty data arrays.
 * It checks for data integrity, pagination correctness, and field-based filtering of discounts.
 *
 * Steps:
 * 1. Create at least 3 discount records with unique field values.
 * 2. PATCH /paymentsDiscounts/discounts: No filter (all records, default pagination).
 * 3. PATCH /paymentsDiscounts/discounts: Filter by discount_rule_id (single-item result).
 * 4. PATCH /paymentsDiscounts/discounts: Filter by order_id.
 * 5. PATCH /paymentsDiscounts/discounts: Filter by amount range.
 * 6. PATCH /paymentsDiscounts/discounts: Filter by notes (if supported).
 * 7. PATCH /paymentsDiscounts/discounts: Pagination using limit=2 (get page 1 and 2).
 * 8. PATCH /paymentsDiscounts/discounts: Use a filter that should yield no results.
 * 9. Assert response structures and correctness at every step.
 */
export async function test_api_discount_list_search_discounts_with_filters_pagination(connection: api.IConnection): Promise<void> {
  // Step 1. Create at least 3 discount records with unique field values.
  const baseDiscounts: IDiscount[] = [];
  for(let i=0; i<3; ++i) {
    const input: IDiscount.ICreate = {
      discount_rule_id: typia.random<string & typia.tags.Format<"uuid">>(),
      order_id: typia.random<string & typia.tags.Format<"uuid">>(),
      amount: -1000 * (i+1),
      notes: `note${i}`,
    };
    const created: IDiscount = await api.functional.paymentsDiscounts.discounts.post(connection, { body: input });
    typia.assert(created);
    baseDiscounts.push(created);
  }

  // Step 2: No filter  returns all just-created discounts (may include others in db, must include our 3)
  const allResult: IPageIDiscount = await api.functional.paymentsDiscounts.discounts.patch(connection, { body: {} });
  typia.assert(allResult);
  const ourIds = new Set(baseDiscounts.map((d) => d.id));
  TestValidator.equals("allResult contains all created")(true)
    (baseDiscounts.every((rec) => allResult.data.some((f) => f.id === rec.id)));

  // Step 3: Filter by discount_rule_id (single-item result guaranteed by our randoms)
  const byRule: IPageIDiscount = await api.functional.paymentsDiscounts.discounts.patch(connection, {
    body: { discount_rule_id: baseDiscounts[0].discount_rule_id },
  });
  typia.assert(byRule);
  TestValidator.equals("byRule: only one")(1)(byRule.data.filter((x) => x.id === baseDiscounts[0].id).length);

  // Step 4: Filter by order_id
  const byOrder: IPageIDiscount = await api.functional.paymentsDiscounts.discounts.patch(connection, {
    body: { order_id: baseDiscounts[1].order_id },
  });
  typia.assert(byOrder);
  TestValidator.equals("byOrder: only one")(1)(byOrder.data.filter((x) => x.id === baseDiscounts[1].id).length);

  // Step 5: Filter by amount_from/amount_to (pick 2nd record's amount)
  const tgt = baseDiscounts[1].amount;
  const byAmount: IPageIDiscount = await api.functional.paymentsDiscounts.discounts.patch(connection, {
    body: { amount_from: tgt, amount_to: tgt+1 },
  });
  typia.assert(byAmount);
  TestValidator.equals("byAmount: exact")(1)(byAmount.data.filter((x) => x.amount === tgt).length);

  // Step 6: Filter by notes
  const noteVal = baseDiscounts[2].notes!;
  const byNotes: IPageIDiscount = await api.functional.paymentsDiscounts.discounts.patch(connection, {
    body: { notes: noteVal } as any, // notes exists in IDiscount but not explicitly in IRequest, allow if backend exposes notes search
  });
  typia.assert(byNotes);
  // Accepts: either 1 or 0 because backend support is ambiguous for notes.
  TestValidator.equals("byNotes: matches")(true)(byNotes.data.some(x => x.notes === noteVal));

  // Step 7: Pagination  limit=2, get first & second page
  const page1: IPageIDiscount = await api.functional.paymentsDiscounts.discounts.patch(connection, {
    body: { limit: 2, page: 1 },
  });
  typia.assert(page1);
  TestValidator.equals("pagination: page size")(2)(page1.data.length <= 2 ? page1.data.length : 2);
  if(page1.pagination.pages > 1) {
    const page2: IPageIDiscount = await api.functional.paymentsDiscounts.discounts.patch(connection, {
      body: { limit: 2, page: 2 },
    });
    typia.assert(page2);
    TestValidator.equals("pagination: page numbers")(2)(page2.pagination.current);
  }

  // Step 8: Filter yields no result (nonexistent discount_rule_id)
  const none: IPageIDiscount = await api.functional.paymentsDiscounts.discounts.patch(connection, {
    body: { discount_rule_id: typia.random<string & typia.tags.Format<"uuid">>() },
  });
  typia.assert(none);
  TestValidator.equals("no results")(0)(none.data.length);
}

