import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IReviewModeration } from "@ORGANIZATION/PROJECT-api/lib/structures/IReviewModeration";

/**
 * Test updating rationale/notes for an existing review moderation record and validation/error handling.
 *
 * Scenario background: All moderation actions require explicit rationale and full audit evidence. Only rationale is mutable post-facto for compliance annotation. The endpoint must ensure traceability, proper authorization, and audit consistency.
 *
 * Step-by-step process:
 * 1. Create a new moderation record (for scenario setup).
 * 2. Update its rationale by ID; verify response has updated rationale and unchanged immutable fields.
 * 3. Attempt to update a non-existent moderation record (expect 404 error).
 * 4. Attempt to submit an invalid update (e.g., empty rationale) and expect validation feedback or rejection.
 */
export async function test_api_review_moderation_update_rationale_and_error_handling(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a new moderation record
  const moderation: IReviewModeration = await api.functional.communicationReview.reviewModerations.post(
    connection,
    {
      body: {
        review_id: typia.random<string>(),
        moderator_user_id: typia.random<string>(),
        action_type: "remove",
        rationale: "Initial removal for policy violation.",
      } satisfies IReviewModeration.ICreate,
    }
  );
  typia.assert(moderation);

  // 2. Update its rationale by ID
  const updatedRationale = "Updated rationale for audit purposes.";
  const updated: IReviewModeration = await api.functional.communicationReview.reviewModerations.putById(
    connection,
    {
      id: moderation.id,
      body: {
        rationale: updatedRationale,
      } satisfies IReviewModeration.IUpdate,
    }
  );
  typia.assert(updated);
  TestValidator.equals("id after update")(moderation.id)(updated.id);
  TestValidator.equals("review_id remains")(moderation.review_id)(updated.review_id);
  TestValidator.equals("moderator_user_id remains")(moderation.moderator_user_id)(updated.moderator_user_id);
  TestValidator.equals("action_type remains")(moderation.action_type)(updated.action_type);
  TestValidator.equals("rationale updated")(updatedRationale)(updated.rationale);
  TestValidator.equals("created_at immutable")(moderation.created_at)(updated.created_at);

  // 3. Attempt to update a non-existent moderation record (expect 404)
  await TestValidator.httpError("non-existent record should 404")(404)(() =>
    api.functional.communicationReview.reviewModerations.putById(
      connection,
      {
        id: typia.random<string>(),
        body: { rationale: "Should not update." },
      }
    )
  );

  // 4. Attempt to submit an invalid update (empty rationale)
  await TestValidator.error("empty rationale should reject")(() =>
    api.functional.communicationReview.reviewModerations.putById(
      connection,
      {
        id: moderation.id,
        body: { rationale: "" },
      }
    )
  );
}

