import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IPageIReviewSentiment } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIReviewSentiment";
import { IReviewSentiment } from "@ORGANIZATION/PROJECT-api/lib/structures/IReviewSentiment";

/**
 * E2E: Search and retrieve paginated list of review sentiment analysis records
 * with filters/pagination/sorting; validates all functionality and edge cases.
 *
 * Test steps:
 * 1. Create several (3+) review sentiment analysis records with diverse field values.
 * 2. Retrieve all records with default pagination and no filters.
 * 3. Search/filter by review_id.
 * 4. Search/filter by ai_model_version_id and/or sentiment.
 * 5. Filter by score range (min/max).
 * 6. Filter by date range on analyzed_at.
 * 7. Query with non-matching filters for empty set.
 * 8. Test pagination (limit=1), check page navigation & pagination meta.
 * 9. Use extreme/invalid pagination arguments, ensure empty set or proper response.
 * 10. Confirm all API responses adhere to types.
 */
export async function test_api_review_sentiment_list_search_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create 3+ sentiment analysis records with diverse values
  const now = new Date();
  const makeISO = (date: Date) => date.toISOString();
  const [
    sentiment1,
    sentiment2,
    sentiment3
  ]: IReviewSentiment[] = await Promise.all([
    api.functional.communicationReview.reviewSentiments.post(connection, {
      body: {
        review_id: typia.random<string>(),
        ai_model_version_id: typia.random<string>(),
        sentiment: "positive",
        score: 0.9,
        analyzed_at: makeISO(new Date(now.getTime() - 10000000)),
      } satisfies IReviewSentiment.ICreate,
    }),
    api.functional.communicationReview.reviewSentiments.post(connection, {
      body: {
        review_id: typia.random<string>(),
        ai_model_version_id: typia.random<string>(),
        sentiment: "negative",
        score: 0.1,
        analyzed_at: makeISO(new Date(now.getTime() - 5000000)),
      } satisfies IReviewSentiment.ICreate,
    }),
    api.functional.communicationReview.reviewSentiments.post(connection, {
      body: {
        review_id: typia.random<string>(),
        ai_model_version_id: typia.random<string>(),
        sentiment: "neutral",
        score: 0.5,
        analyzed_at: makeISO(now),
      } satisfies IReviewSentiment.ICreate,
    }),
  ]);
  [sentiment1, sentiment2, sentiment3].forEach(typia.assert);

  // 2. Retrieve all records, no filter - should include all above
  const allResult: IPageIReviewSentiment =
    await api.functional.communicationReview.reviewSentiments.patch(connection, {
      body: {},
    });
  typia.assert(allResult);
  const returnedIds = allResult.data.map(r => r.id);
  for (const sent of [sentiment1, sentiment2, sentiment3]) {
    TestValidator.true("record present in unfiltered result")(
      returnedIds.includes(sent.id)
    );
  }

  // 3. Search/filter by a specific review_id
  const resultByReview: IPageIReviewSentiment =
    await api.functional.communicationReview.reviewSentiments.patch(connection, {
      body: { review_id: sentiment1.review_id },
    });
  typia.assert(resultByReview);
  TestValidator.true("filter by review_id, only correct")(
    resultByReview.data.every(r => r.review_id === sentiment1.review_id)
  );

  // 4. Filter by ai_model_version_id and sentiment
  const byModelVer: IPageIReviewSentiment =
    await api.functional.communicationReview.reviewSentiments.patch(connection, {
      body: {
        ai_model_version_id: sentiment2.ai_model_version_id,
        sentiment: sentiment2.sentiment,
      },
    });
  typia.assert(byModelVer);
  TestValidator.true("ai_model/sentiment filter correct")(
    byModelVer.data.every(r =>
      r.ai_model_version_id === sentiment2.ai_model_version_id &&
      r.sentiment === sentiment2.sentiment
    )
  );

  // 5. Score range
  const scoreRange: IPageIReviewSentiment =
    await api.functional.communicationReview.reviewSentiments.patch(connection, {
      body: { score_min: 0.6, score_max: 1 },
    });
  typia.assert(scoreRange);
  TestValidator.true("score range filter correct")(
    scoreRange.data.every(r => r.score >= 0.6 && r.score <= 1)
  );

  // 6. Date range
  const dateFrom = makeISO(new Date(now.getTime() - 6000000));
  const dateTo = makeISO(now);
  const dateRange: IPageIReviewSentiment =
    await api.functional.communicationReview.reviewSentiments.patch(connection, {
      body: { analyzed_at_from: dateFrom, analyzed_at_to: dateTo },
    });
  typia.assert(dateRange);
  TestValidator.true("date range filter correct")(
    dateRange.data.every(
      r =>
        r.analyzed_at >= dateFrom &&
        r.analyzed_at <= dateTo
    )
  );

  // 7. Non-matching filter yields empty set
  const nonMatching: IPageIReviewSentiment =
    await api.functional.communicationReview.reviewSentiments.patch(connection, {
      body: { review_id: typia.random<string>() },
    });
  typia.assert(nonMatching);
  TestValidator.equals("empty on no match")(0)(nonMatching.data.length);

  // 8. Pagination limit=1
  const paged: IPageIReviewSentiment =
    await api.functional.communicationReview.reviewSentiments.patch(connection, {
      body: { limit: 1 },
    });
  typia.assert(paged);
  TestValidator.equals("pagination limit=1")(1)(paged.data.length);
  TestValidator.equals("pagination limit meta")(
    1
  )(paged.pagination.limit);

  // 9. Extreme/invalid pagination args
  const bigPage: IPageIReviewSentiment =
    await api.functional.communicationReview.reviewSentiments.patch(connection, {
      body: { page: 10000 },
    });
  typia.assert(bigPage);
  TestValidator.equals("big page = empty or not crash")(0)(bigPage.data.length);

  const zeroLimit: IPageIReviewSentiment =
    await api.functional.communicationReview.reviewSentiments.patch(connection, {
      body: { limit: 0 },
    });
  typia.assert(zeroLimit);
  TestValidator.equals("limit zero = empty")(0)(zeroLimit.data.length);

  const negativeLimit: IPageIReviewSentiment =
    await api.functional.communicationReview.reviewSentiments.patch(connection, {
      body: { limit: -1 },
    });
  typia.assert(negativeLimit);
  TestValidator.equals("limit negative = empty")(0)(negativeLimit.data.length);
}

