import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IPageISentimentAnalysisLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageISentimentAnalysisLog";
import { ISentimentAnalysisLog } from "@ORGANIZATION/PROJECT-api/lib/structures/ISentimentAnalysisLog";

/**
 * Fetch a filtered/paginated list of sentiment analysis logs.
 *
 * This test validates the ability to retrieve pages of sentiment analysis log entries
 * using advanced filtering and pagination criteria. It covers the following:
 * - Ingests several log entries with varying review_ids, AI model versions, and timestamps
 * - Validates that search/filter by specific fields and value patterns yields correct results
 * - Ensures pagination (offset/page & limit/size) works, including edge cases (empty, high offset)
 *
 * Steps:
 * 1. Create multiple sentiment analysis log records with varying review_ids, ai_model_version_ids, and timestamps
 * 2. Query the logs using various filters (by review_id, ai_model_version_id, model_version, date ranges, input_match).
 * 3. Test pagination: request page 1 and a later page; test high limit/offset to verify correctness.
 * 4. Edge case: attempt a search that should return no results, and verify an empty data array with valid pagination.
 * 5. For each valid search, assert the returned logs strictly match the search filter.
 */
export async function test_api_sentiment_analysis_log_list_search_filter_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Insert multiple sentiment analysis log records (minimum 5, with diverse fields)
  const testCases = [
    {
      review_id: typia.random<string & typia.tags.Format<"uuid">>(),
      ai_model_version_id: typia.random<string & typia.tags.Format<"uuid">>(),
      model_version: "model_a:1.0.0",
      raw_input: "Hello world",
      output: "POSITIVE",
      analyzed_at: new Date(Date.now() - 500000).toISOString(),
    },
    {
      review_id: typia.random<string & typia.tags.Format<"uuid">>(),
      ai_model_version_id: typia.random<string & typia.tags.Format<"uuid">>(),
      model_version: "model_b:2.1.0",
      raw_input: "This is a bad sample",
      output: "NEGATIVE",
      analyzed_at: new Date(Date.now() - 400000).toISOString(),
    },
    {
      review_id: typia.random<string & typia.tags.Format<"uuid">>(),
      ai_model_version_id: typia.random<string & typia.tags.Format<"uuid">>(),
      model_version: "model_a:1.0.0",
      raw_input: "Neutral content",
      output: "NEUTRAL",
      analyzed_at: new Date(Date.now() - 300000).toISOString(),
    },
    {
      review_id: typia.random<string & typia.tags.Format<"uuid">>(),
      ai_model_version_id: typia.random<string & typia.tags.Format<"uuid">>(),
      model_version: "model_b:2.1.0",
      raw_input: "Awesome product!",
      output: "POSITIVE",
      analyzed_at: new Date(Date.now() - 200000).toISOString(),
    },
    {
      review_id: typia.random<string & typia.tags.Format<"uuid">>(),
      ai_model_version_id: typia.random<string & typia.tags.Format<"uuid">>(),
      model_version: "model_a:1.0.0",
      raw_input: "Terrible and slow",
      output: "NEGATIVE",
      analyzed_at: new Date(Date.now() - 100000).toISOString(),
    },
  ];
  const inserted: ISentimentAnalysisLog[] = [];
  for (const body of testCases) {
    const result = await api.functional.communicationReview.sentimentAnalysisLogs.post(connection, { body: body satisfies ISentimentAnalysisLog.ICreate });
    typia.assert(result);
    inserted.push(result);
  }

  // 2. Query by one field (review_id)
  {
    const target = inserted[0];
    const response = await api.functional.communicationReview.sentimentAnalysisLogs.patch(connection, {
      body: { review_id: target.review_id } satisfies ISentimentAnalysisLog.IRequest,
    });
    typia.assert(response);
    for (const log of response.data) {
      TestValidator.equals("review_id should match")(target.review_id)(log.review_id);
    }
  }

  // 3. Query by ai_model_version_id and model_version
  {
    const target = inserted[1];
    const response = await api.functional.communicationReview.sentimentAnalysisLogs.patch(connection, {
      body: {
        ai_model_version_id: target.ai_model_version_id,
        model_version: target.model_version,
      } satisfies ISentimentAnalysisLog.IRequest,
    });
    typia.assert(response);
    for (const log of response.data) {
      TestValidator.equals("ai_model_version_id")(target.ai_model_version_id)(log.ai_model_version_id);
      TestValidator.equals("model_version")(target.model_version)(log.model_version);
    }
  }

  // 4. Query by input_match (partial string)
  {
    const target = inserted[2];
    const match = target.raw_input.substring(0, 6); // partial prefix
    const response = await api.functional.communicationReview.sentimentAnalysisLogs.patch(connection, {
      body: { input_match: match } satisfies ISentimentAnalysisLog.IRequest,
    });
    typia.assert(response);
    TestValidator.equals("Contains at least one matching partial input")(
      true,
    )(response.data.some(l => l.raw_input.includes(match)));
  }

  // 5. Query by time range
  {
    const minTime = inserted[3].analyzed_at;
    const response = await api.functional.communicationReview.sentimentAnalysisLogs.patch(connection, {
      body: {
        time_from: minTime,
        time_to: new Date().toISOString(),
      } satisfies ISentimentAnalysisLog.IRequest,
    });
    typia.assert(response);
    for (const log of response.data) {
      TestValidator.equals("All logs analyzed_at in requested range")(
        true,
      )(
        log.analyzed_at >= minTime && log.analyzed_at <= new Date().toISOString(),
      );
    }
  }

  // 6. Pagination - page 1, small limit
  {
    const response = await api.functional.communicationReview.sentimentAnalysisLogs.patch(connection, {
      body: { page: 1, limit: 2 } satisfies ISentimentAnalysisLog.IRequest,
    });
    typia.assert(response);
    TestValidator.equals("Pagination, results length")(2)(response.data.length);
    TestValidator.equals("pagination.limit properly set")(2)(response.pagination.limit);
  }

  // 7. Pagination - high page/offset, expect empty data with correct pagination
  {
    const response = await api.functional.communicationReview.sentimentAnalysisLogs.patch(connection, {
      body: { page: 100, limit: 10 } satisfies ISentimentAnalysisLog.IRequest,
    });
    typia.assert(response);
    TestValidator.equals("Pagination - empty data array, valid pagination")(
      0
    )(response.data.length);
    TestValidator.equals("pagination.limit properly set")(
      10
    )(response.pagination.limit);
  }

  // 8. Filter which returns no result
  {
    const response = await api.functional.communicationReview.sentimentAnalysisLogs.patch(connection, {
      body: { model_version: "non_existing_version" } satisfies ISentimentAnalysisLog.IRequest,
    });
    typia.assert(response);
    TestValidator.equals("No results for impossible model_version")(
      0,
    )(response.data.length);
    TestValidator.equals("pagination structure exists")(
      true,
    )(!!response.pagination);
  }
}

import { TestValidator } from "@nestia/e2e";
