/**
 * Validate the update, error handling, and auditing of sentiment analysis logs.
 *
 * This test ensures that updates to sentiment analysis evidence logs via PUT are enforced under regulatory integrity and audit traceability. 
 * Steps include successful update, handling of non-existent resource (404), and validation error checks.
 *
 * 1. Create a sentiment analysis log entry for update testing
 * 2. Update metadata (output, model_version, analyzed_at) for the existing log and validate audit evidence
 * 3. Try to update a non-existent log entry (random UUID): expect 404 response
 * 4. Supply invalid update values (malformed date-time, empty string): expect input validation error
 */
export async function test_api_sentiment_analysis_log_update_validation_and_existence(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a sentiment analysis log entry
  const createInput: ISentimentAnalysisLog.ICreate = {
    review_id: typia.random<string>(),
    ai_model_version_id: typia.random<string>(),
    raw_input: "bayesian+sentiment: { text: 'hello world' }",
    output: JSON.stringify({ label: "positive", score: 0.99 }),
    model_version: "v1.2.3-audit",
    analyzed_at: new Date().toISOString(),
  };
  const created: ISentimentAnalysisLog =
    await api.functional.communicationReview.sentimentAnalysisLogs.post(
      connection,
      {
        body: createInput,
      }
    );
  typia.assert(created);

  // 2. Update the log's output, version, and analyzed_at timestamp
  const updateInput: ISentimentAnalysisLog.IUpdate = {
    output: JSON.stringify({ label: "negative", score: 0.1 }),
    model_version: "v1.2.4-evidence",
    analyzed_at: new Date(Date.now() + 3600 * 1000).toISOString(), // +1 hour
  };
  const updated: ISentimentAnalysisLog =
    await api.functional.communicationReview.sentimentAnalysisLogs.putById(
      connection,
      {
        id: created.id,
        body: updateInput,
      },
    );
  typia.assert(updated);
  TestValidator.equals("id should be unchanged")(created.id)(updated.id);
  TestValidator.equals("review_id should be unchanged")(created.review_id)(updated.review_id);
  TestValidator.equals("updated output")(updateInput.output)(updated.output);
  TestValidator.equals("updated model_version")(updateInput.model_version)(updated.model_version);
  TestValidator.equals("updated analyzed_at")(updateInput.analyzed_at)(updated.analyzed_at);

  // 3. Attempt to update a non-existent log entry
  await TestValidator.httpError("update non-existent log should return 404")(404)(() =>
    api.functional.communicationReview.sentimentAnalysisLogs.putById(
      connection,
      {
        id: typia.random<string>(),
        body: {
          output: "forced failure",
        },
      },
    )
  );

  // 4. Try invalid input: invalid date format and missing output/model_version
  await TestValidator.error("invalid analyzed_at format should fail")(() =>
    api.functional.communicationReview.sentimentAnalysisLogs.putById(
      connection,
      {
        id: created.id,
        body: {
          analyzed_at: "invalid-datetime",
        },
      },
    )
  );
  await TestValidator.error("invalid: empty output string should fail")(() =>
    api.functional.communicationReview.sentimentAnalysisLogs.putById(
      connection,
      {
        id: created.id,
        body: {
          output: "",
        },
      },
    )
  );
}

