import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IFavorite } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavorite";
import { IFavoritesPersonalization } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavoritesPersonalization";
import { IPageIFavorite } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIFavorite";

/**
 * Retrieve a paginated, filterable list of favorite records by different search criteria.
 *
 * This test covers searching for favorites by user, by product, by both user and product, and checks pagination.
 * It validates the "PATCH /favoritesPersonalization/favorites" endpoint returns correct filtered and paginated results and handles edge cases including empty result sets.
 *
 * Steps:
 * 1. Create multiple favorite records (at least two users and multiple products, covering various combinations for search).
 * 2. Execute search (PATCH /favoritesPersonalization/favorites) with no filters; confirm all created records appear, paginated.
 * 3. Execute search filtering by user_id; only that user's favorites are returned.
 * 4. Execute search filtering by product_id; only records for that product are returned.
 * 5. Execute search with both user_id and product_id; confirm single record returned if exists.
 * 6. Search with pagination (e.g., limit=2); confirm ordering, limit, and next page availability.
 * 7. Search with pagination specifying higher page indexes; confirm empty data when page is out-of-range.
 * 8. Test deleted=false and deleted=true (if logical delete is implemented).
 */
export async function test_api_favorite_list_search_favorites_with_various_filters_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create multiple favorite records (2 users x 2 products)
  const users = [typia.random<string>(), typia.random<string>()];
  const products = [typia.random<string>(), typia.random<string>()];
  const favorites: IFavoritesPersonalization.IFavorite[] = [];
  for (const user_id of users) {
    for (const product_id of products) {
      const favorite = await api.functional.favoritesPersonalization.favorites.post(connection, {
        body: { user_id, product_id } satisfies IFavoritesPersonalization.IFavoriteCreate,
      });
      typia.assert(favorite);
      favorites.push(favorite);
    }
  }

  // 2. Search with no filters
  let res: IPageIFavorite = await api.functional.favoritesPersonalization.favorites.patch(connection, {
    body: {},
  });
  typia.assert(res);
  TestValidator.equals("all favorites count")(favorites.length)(res.data.length);
  const allIds = favorites.map(f => f.id).sort();
  const resIds = res.data.map(f => f.id).sort();
  TestValidator.equals("all favorites ids")(allIds)(resIds);

  // 3. Search by user_id
  for (const user_id of users) {
    res = await api.functional.favoritesPersonalization.favorites.patch(connection, {
      body: { user_id },
    });
    typia.assert(res);
    for (const fav of res.data) {
      TestValidator.equals("filter by user_id")(user_id)(fav.user_id);
    }
  }

  // 4. Search by product_id
  for (const product_id of products) {
    res = await api.functional.favoritesPersonalization.favorites.patch(connection, {
      body: { product_id },
    });
    typia.assert(res);
    for (const fav of res.data) {
      TestValidator.equals("filter by product_id")(product_id)(fav.product_id);
    }
  }

  // 5. Search by user_id + product_id
  for (const user_id of users) {
    for (const product_id of products) {
      res = await api.functional.favoritesPersonalization.favorites.patch(connection, {
        body: { user_id, product_id },
      });
      typia.assert(res);
      if (res.data.length > 0) {
        for (const fav of res.data) {
          TestValidator.equals("filter by user and product")(user_id)(fav.user_id);
          TestValidator.equals("filter by user and product")(product_id)(fav.product_id);
        }
        TestValidator.equals("single favorite for user+product")(1)(res.data.length);
      }
    }
  }

  // 6. Pagination: limit=2
  res = await api.functional.favoritesPersonalization.favorites.patch(connection, {
    body: { limit: 2 },
  });
  typia.assert(res);
  TestValidator.equals("pagination limit")(2)(res.data.length);
  TestValidator.equals("pagination limit metadata")(2)(res.pagination.limit);

  // 7. Out-of-range page
  const highPage = 99;
  res = await api.functional.favoritesPersonalization.favorites.patch(connection, {
    body: { page: highPage, limit: 2 },
  });
  typia.assert(res);
  TestValidator.equals("out of range empty")(0)(res.data.length);

  // 8. Test deleted flag if logical delete implemented
  res = await api.functional.favoritesPersonalization.favorites.patch(connection, {
    body: { deleted: false },
  });
  typia.assert(res);
  // All created favorites should be active (not deleted)
  TestValidator.equals("active only")(favorites.length)(res.data.length);
  res = await api.functional.favoritesPersonalization.favorites.patch(connection, {
    body: { deleted: true },
  });
  typia.assert(res);
  // Should be empty as no record was soft-deleted yet
  TestValidator.equals("deleted only")(0)(res.data.length);
}

