import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IFavoritesPersonalization } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavoritesPersonalization";

/**
 * Validate fetching a favorite record by valid and invalid ID.
 *
 * This test ensures that the API correctly returns the favorite record when using a valid ID, and responds with a 404 error when an invalid/non-existent ID is used.
 *
 * Steps:
 * 1. Create a favorite record with valid user_id and product_id via POST /favoritesPersonalization/favorites.
 * 2. Fetch this favorite by its id using GET /favoritesPersonalization/favorites/{id}; assert fields are consistent.
 * 3. Attempt to fetch a favorite by a random non-existent id and assert 404 response.
 */
export async function test_api_favorite_get_by_valid_and_invalid_id(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create favorite
  const candidate: IFavoritesPersonalization.IFavoriteCreate = {
    user_id: typia.random<string & tags.Format<"uuid">>(),
    product_id: typia.random<string & tags.Format<"uuid">>(),
  };
  const created: IFavoritesPersonalization.IFavorite =
    await api.functional.favoritesPersonalization.favorites.post(
      connection,
      { body: candidate },
    );
  typia.assert(created);

  // 2. Fetch favorite by valid id and assert fields
  const fetched: IFavoritesPersonalization.IFavorite =
    await api.functional.favoritesPersonalization.favorites.getById(
      connection,
      { id: created.id },
    );
  typia.assert(fetched);
  TestValidator.equals("user_id")(created.user_id)(fetched.user_id);
  TestValidator.equals("product_id")(created.product_id)(fetched.product_id);
  TestValidator.equals("id")(created.id)(fetched.id);
  TestValidator.equals("created_at")(created.created_at)(fetched.created_at);
  TestValidator.equals("deleted_at")(created.deleted_at ?? null)(fetched.deleted_at ?? null);

  // 3. Attempt to fetch non-existent favorite by random id (should fail with 404)
  await TestValidator.httpError("favorite not found")(404)(() =>
    api.functional.favoritesPersonalization.favorites.getById(
      connection,
      { id: typia.random<string & tags.Format<"uuid">>() },
    ),
  );
}

import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IFavoritesPersonalization } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavoritesPersonalization";

/**
 * Validate creation of a favorite record and error scenarios.
 *
 * This test covers the following:
 * 1. Successfully creating a favorite record with valid user_id/product_id
 * 2. Attempting to create a favorite with missing or invalid fields and expecting validation errors
 * 3. Attempting to create a duplicate favorite (same user_id, product_id) and confirming uniqueness constraint is enforced
 *
 * Steps:
 * 1. Generate valid favorite creation data and create the record
 * 2. Assert returned favorite object has matching data and required structure
 * 3. Try to create favorites with missing user_id, missing product_id, and invalid user_id format, expect validation errors
 * 4. Attempt creation with the same user_id/product_id (duplicate), expect a conflict error (e.g., HTTP 409)
 */
export async function test_api_favorites_create_success_and_failure(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a valid favorite
  const favoriteInput: IFavoritesPersonalization.IFavoriteCreate = {
    user_id: typia.random<string & typia.tags.Format<"uuid">>(),
    product_id: typia.random<string & typia.tags.Format<"uuid">>(),
  };
  const favorite = await api.functional.favoritesPersonalization.favorites.post(connection, {
    body: favoriteInput,
  });
  typia.assert(favorite);
  TestValidator.equals("favorite user_id")(favoriteInput.user_id)(favorite.user_id);
  TestValidator.equals("favorite product_id")(favoriteInput.product_id)(favorite.product_id);

  // 2. Missing user_id
  await TestValidator.httpError("missing user_id")(400)(() =>
    api.functional.favoritesPersonalization.favorites.post(connection, {
      body: { product_id: favoriteInput.product_id } as any,
    })
  );

  // 3. Missing product_id
  await TestValidator.httpError("missing product_id")(400)(() =>
    api.functional.favoritesPersonalization.favorites.post(connection, {
      body: { user_id: favoriteInput.user_id } as any,
    })
  );

  // 4. Invalid user_id format
  await TestValidator.httpError("invalid user_id format")(400)(() =>
    api.functional.favoritesPersonalization.favorites.post(connection, {
      body: { user_id: "not-a-uuid", product_id: favoriteInput.product_id },
    })
  );

  // 5. Duplicate favorite (same user_id/product_id)
  await TestValidator.httpError("duplicate favorite")(409)(() =>
    api.functional.favoritesPersonalization.favorites.post(connection, {
      body: favoriteInput,
    })
  );
}

import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IFavoritesPersonalization } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavoritesPersonalization";

/**
 * Validate updating a favorite record with correct and erroneous inputs.
 *
 * Scenario steps:
 * 1. Create a favorite record.
 * 2. Update the record with a valid update (e.g., set deleted_at).
 * 3. Attempt to update the record with invalid/malformed data and expect validation errors.
 * 4. Attempt to update a non-existent favorite id and expect a 404 error.
 */
export async function test_api_favoritesPersonalization_favorites_update_validation_and_nonexistent_id(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a favorite record to operate on
  const favorite: IFavoritesPersonalization.IFavorite = await api.functional.favoritesPersonalization.favorites.post(connection, {
    body: {
      user_id: typia.random<string & typia.tags.Format<"uuid">>(),
      product_id: typia.random<string & typia.tags.Format<"uuid">>(),
    } satisfies IFavoritesPersonalization.IFavoriteCreate,
  });
  typia.assert(favorite);

  // 2. Update the favorite with a valid update (set deleted_at)
  const deletedAt = new Date().toISOString();
  const updatedFavorite: IFavoritesPersonalization.IFavorite = await api.functional.favoritesPersonalization.favorites.putById(connection, {
    id: favorite.id,
    body: { deleted_at: deletedAt } satisfies IFavoritesPersonalization.IFavoriteUpdate,
  });
  typia.assert(updatedFavorite);
  TestValidator.equals("deleted_at should be updated")(deletedAt)(updatedFavorite.deleted_at);

  // 3. Attempt to update with invalid data (malformed deleted_at)
  await TestValidator.httpError("update with invalid deleted_at format should fail")(400)(() =>
    api.functional.favoritesPersonalization.favorites.putById(connection, {
      id: favorite.id,
      body: { deleted_at: "not-a-valid-date" as any },
    })
  );

  // 4. Attempt to update a non-existent favorite id
  await TestValidator.httpError("update with non-existent id should fail")(404)(() =>
    api.functional.favoritesPersonalization.favorites.putById(connection, {
      id: "aaaaaaaa-aaaa-aaaa-aaaa-aaaaaaaaaaaa", // unlikely static UUID
      body: { deleted_at: new Date().toISOString() },
    })
  );
}

import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IFavoritesPersonalization } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavoritesPersonalization";

/**
 * Soft-delete a favorite record by ID and validate evidence/audit retention.
 *
 * 1. Create a favorite record for deletion.
 * 2. Soft-delete the favorite record by its ID.
 * 3. Validate that the response contains the expected soft-deleted record (deleted_at is set).
 * 4. Try deleting a non-existent record and expect a 404 error response.
 */
export async function test_api_favoritesPersonalization_favorites_soft_delete_and_invalid_id(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a favorite record for deletion
  const favorite: IFavoritesPersonalization.IFavorite =
    await api.functional.favoritesPersonalization.favorites.post(connection, {
      body: {
        user_id: typia.random<string & typia.tags.Format<"uuid">>(),
        product_id: typia.random<string & typia.tags.Format<"uuid">>(),
      },
    });
  typia.assert(favorite);

  // 2. Soft-delete the favorite record by its ID
  const deleted: IFavoritesPersonalization.IFavorite =
    await api.functional.favoritesPersonalization.favorites.eraseById(connection, {
