import typia from "typia";
import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IFavoritesPersonalization } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavoritesPersonalization";
import { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";

/**
 * List and search bookmarks with various filtering and pagination
 *
 * This test validates the list/search and pagination behavior of bookmarks in the favoritesPersonalization domain.
 *
 * Steps:
 * 1. Create multiple bookmarks with different user_ids, product_ids, custom_folder, tag, and note via POST /favoritesPersonalization/bookmarks.
 * 2. Search bookmarks by user_id, product_id, custom_folder, tag, and note substring, confirming returned results match the filter.
 * 3. Test with empty filter (no criteria) to ensure all bookmarks appear (subject to default pagination).
 * 4. Test pagination edge case: request with a very high page and large limit, expecting empty or partial results and valid metadata.
 * 5. Validate that all responses are correctly structured and match IFavoritesPersonalization.PageIBookmark and row fields.
 */
export async function test_api_favoritesPersonalization_bookmarks_list_search_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create bookmarks
  const users = [typia.random<string>(), typia.random<string>()];
  const products = [typia.random<string>(), typia.random<string>()];
  const folders = ["Work", "Personal", undefined];
  const tags = ["urgent", "fun", undefined];

  const createdBookmarks: IFavoritesPersonalization.IBookmark[] = [];
  for (let u = 0; u < users.length; ++u) {
    for (let p = 0; p < products.length; ++p) {
      for (let f = 0; f < folders.length; ++f) {
        for (let t = 0; t < tags.length; ++t) {
          const note = t === 1 ? "Important notes for testing" : undefined;
          const req: IFavoritesPersonalization.IBookmarkCreate = {
            user_id: users[u],
            product_id: products[p],
            custom_folder: folders[f],
            tag: tags[t],
            note,
          };
          const bookmark = await api.functional.favoritesPersonalization.bookmarks.post(connection, { body: req });
          typia.assert(bookmark);
          createdBookmarks.push(bookmark);
        }
      }
    }
  }

  // 2. Filter: by user_id
  {
    const userId = users[0];
    const output = await api.functional.favoritesPersonalization.bookmarks.patch(connection, { body: { user_id: userId } });
    typia.assert(output);
    for (const b of output.data) {
      TestValidator.equals("user_id filter")(userId)(b.user_id);
    }
  }
  // 3. Filter: by product_id
  {
    const productId = products[1];
    const output = await api.functional.favoritesPersonalization.bookmarks.patch(connection, { body: { product_id: productId } });
    typia.assert(output);
    for (const b of output.data) {
      TestValidator.equals("product_id filter")(productId)(b.product_id);
    }
  }
  // 4. Filter: custom_folder
  {
    const folder = folders[0];
    const output = await api.functional.favoritesPersonalization.bookmarks.patch(connection, { body: { custom_folder: folder } });
    typia.assert(output);
    for (const b of output.data) {
      TestValidator.equals("folder filter")(folder)(b.custom_folder);
    }
  }
  // 5. Filter: tag and note substring
  {
    const tag = "urgent";
    const noteSubstr = "notes";
    const output = await api.functional.favoritesPersonalization.bookmarks.patch(connection, { body: { tag, note: noteSubstr } });
    typia.assert(output);
    for (const b of output.data) {
      if (b.tag) TestValidator.equals("tag filter")(tag)(b.tag);
      if (b.note) TestValidator.between("note substring")(1)(b.note.indexOf(noteSubstr));
    }
  }
  // 6. Empty filter: get all (subject to pagination)
  {
    const output = await api.functional.favoritesPersonalization.bookmarks.patch(connection, { body: {} });
    typia.assert(output);
  }
  // 7. Pagination edge: very high page & limit
  {
    const output = await api.functional.favoritesPersonalization.bookmarks.patch(connection, {
      body: { page: 100, limit: 50 },
    });
    typia.assert(output);
    TestValidator.equals("pagination.current")(100)(output.pagination.current);
    TestValidator.equals("pagination.limit")(50)(output.pagination.limit);
    // likely output.data.length === 0, check for valid structure
    TestValidator.equals("pagination data length")(output.data.length)(output.data.length);
  }
}

