import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IFavoritesPersonalization } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavoritesPersonalization";

/**
 * Test bookmark creation with valid and invalid input cases.
 *
 * This scenario validates both successful creation and correct error handling
 * on invalid input for the user personalization bookmarks API. Bookmarks must
 * be unique for a user-product combination and conform to all structural/type
 * rules. Upon valid submission, the result is type-checked. Upon
 * invalid/malformed submission, validation errors are asserted.
 *
 * 1. Create a bookmark with all required/valid properties (should succeed).
 * 2. Attempt to create a bookmark with missing required user_id (should fail).
 * 3. Attempt to create a bookmark with missing required product_id (should fail).
 * 4. Attempt to create a bookmark with non-UUID user_id (should fail).
 * 5. Attempt to create a bookmark with non-UUID product_id (should fail).
 *
 * Tests both positive flow and validation error enforcement by the backend.
 */
export async function test_api_bookmark_create_valid_and_invalid(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a valid bookmark
  const validInput: IFavoritesPersonalization.IBookmarkCreate = {
    user_id: "2846d00a-1234-4ef3-b6f3-92aecfa1c001",
    product_id: "d3d78728-5132-4973-8d32-8a6eb9515872",
    custom_folder: "Sample Folder",
    tag: "test-tag",
    note: "Some note"
  };
  const output = await api.functional.favoritesPersonalization.bookmarks.post(
    connection,
    { body: validInput },
  );
  typia.assert(output);
  TestValidator.equals("user_id")(validInput.user_id)(output.user_id);
  TestValidator.equals("product_id")(validInput.product_id)(output.product_id);
  TestValidator.equals("custom_folder")(validInput.custom_folder)(output.custom_folder);
  TestValidator.equals("tag")(validInput.tag)(output.tag);
  TestValidator.equals("note")(validInput.note)(output.note);

  // 2. Missing user_id (should fail)
  await TestValidator.error("missing user_id")(() =>
    api.functional.favoritesPersonalization.bookmarks.post(connection, {
      body: {
        // user_id intentionally omitted
        product_id: "d3d78728-5132-4973-8d32-8a6eb9515872",
        custom_folder: "Folder",
        tag: "a",
        note: "b"
      } as any,
    })
  );

  // 3. Missing product_id (should fail)
  await TestValidator.error("missing product_id")(() =>
    api.functional.favoritesPersonalization.bookmarks.post(connection, {
      body: {
        user_id: "2846d00a-1234-4ef3-b6f3-92aecfa1c001",
        // product_id omitted
        custom_folder: "Folder",
        tag: "a",
        note: "b"
      } as any,
    })
  );

  // 4. Non-UUID user_id
  await TestValidator.error("non-UUID user_id")(() =>
    api.functional.favoritesPersonalization.bookmarks.post(connection, {
      body: {
        user_id: "not-a-uuid",
        product_id: "d3d78728-5132-4973-8d32-8a6eb9515872",
      } as any,
    })
  );

  // 5. Non-UUID product_id
  await TestValidator.error("non-UUID product_id")(() =>
    api.functional.favoritesPersonalization.bookmarks.post(connection, {
      body: {
        user_id: "2846d00a-1234-4ef3-b6f3-92aecfa1c001",
        product_id: "not-a-uuid",
      } as any,
    })
  );
}

import { TestValidator } from "@nestia/e2e";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IFavoritesPersonalization } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavoritesPersonalization";

/**
 * Test updating an existing bookmark and error cases.
 *
 * This test covers both successful update of a bookmark and several failure scenarios:
 * - Ensures updates to metadata fields (custom_folder, tag, note) are saved and can be retrieved.
 * - Tries updating a non-existent bookmark (invalid ID), expecting an error response.
 * - Tries updating with invalid fields, expecting validation errors from the API.
 *
 * Steps:
 * 1. Create a valid bookmark for update target.
 * 2. Update bookmark with new metadata (custom_folder, tag, note).
 * 3. Retrieve and confirm bookmark changes.
 * 4. Attempt update with non-existent ID, expect error.
 * 5. Attempt update with invalid fields, expect validation error.
 */
export async function test_api_favoritesPersonalization_bookmarks_update_valid_and_invalid(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a bookmark
  const bookmarkCreate: IFavoritesPersonalization.IBookmarkCreate = {
    user_id: typia.random<string & tags.Format<"uuid">>(),
    product_id: typia.random<string & tags.Format<"uuid">>(),
    custom_folder: "initial-folder",
    tag: "important",
    note: "Original note."
  };
  const bookmark: IFavoritesPersonalization.IBookmark = await api.functional.favoritesPersonalization.bookmarks.post(
    connection,
    { body: bookmarkCreate },
  );
  typia.assert(bookmark);
  TestValidator.equals("custom_folder after creation")("initial-folder")(bookmark.custom_folder);
  TestValidator.equals("tag after creation")("important")(bookmark.tag);
  TestValidator.equals("note after creation")("Original note.")(bookmark.note);

  // 2. Update the existing bookmark with new valid data
  const updateInput: IFavoritesPersonalization.IBookmarkUpdate = {
    custom_folder: "updated-folder",
    tag: "reviewed",
    note: "Updated note."
  };
  const updated: IFavoritesPersonalization.IBookmark = await api.functional.favoritesPersonalization.bookmarks.putById(
    connection,
    { id: bookmark.id, body: updateInput },
  );
  typia.assert(updated);
  TestValidator.equals("custom_folder updated")("updated-folder")(updated.custom_folder);
  TestValidator.equals("tag updated")("reviewed")(updated.tag);
  TestValidator.equals("note updated")("Updated note.")(updated.note);

  // 3. Query the bookmark again to confirm update (simulate by calling putById again and checking fields)
  const reFetched: IFavoritesPersonalization.IBookmark = await api.functional.favoritesPersonalization.bookmarks.putById(
    connection,
    { id: bookmark.id, body: {} }, // no-op update, just fetch
  );
  typia.assert(reFetched);
  TestValidator.equals("custom_folder fetched again")("updated-folder")(reFetched.custom_folder);
  TestValidator.equals("tag fetched again")("reviewed")(reFetched.tag);
  TestValidator.equals("note fetched again")("Updated note.")(reFetched.note);

  // 4. Attempt to update using a non-existent bookmark ID
  await TestValidator.httpError("update non-existent bookmark")(404)(() =>
    api.functional.favoritesPersonalization.bookmarks.putById(
      connection,
      {
        id: typia.random<string & tags.Format<"uuid">>(), // very likely to not exist
        body: updateInput,
      },
    ),
  );

  // 5. Attempt update with invalid fields (wrong types, missing requirements)
  await TestValidator.httpError("invalid update payload: not string for custom_folder")
    (400)(() =>
      api.functional.favoritesPersonalization.bookmarks.putById(
        connection,
        {
          id: bookmark.id,
          body: {
            custom_folder: 123 as any,
            tag: updateInput.tag,
            note: updateInput.note,
          }
        },
      ),
    );
  await TestValidator.httpError("invalid update payload: deleted_at wrong format")
    (400)(() =>
      api.functional.favoritesPersonalization.bookmarks.putById(
        connection,
        {
          id: bookmark.id,
          body: { deleted_at: "not-a-date" as any },
        },
      ),
    );
}

