 * 2. Update the feature store with valid new data and verify changes.
 * 3. Fetch latest state (using PUT output as no GET provided).
 * 4. Attempt update with invalid ID (expect error).
 * 5. Attempt update with broken payload (expect validation error).
 */
export async function test_api_ai_feature_store_update(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create AI feature store record
  const original: IAIFeatureStore = await api.functional.advancedAI.aiFeatureStore.post(connection, {
    body: {
      ai_model_version_id: typia.random<string>(),
      ai_training_data_id: null,
      name: "Initial Feature Set",
      description: "Original features for model.",
      schema: '{"fields":[{"name":"f1","type":"float"}]}',
    },
  });
  typia.assert(original);

  // 2. Update with valid changes
  const upd: IAIFeatureStore = await api.functional.advancedAI.aiFeatureStore.putById(connection, {
    id: original.id,
    body: {
      name: "Updated Feature Set",
      description: "Updated description.",
      schema: '{"fields":[{"name":"f1","type":"float"},{"name":"f2","type":"int"}]}',
    },
  });
