  TestValidator.equals("id not changed")(original.id)(upd.id);
  TestValidator.equals("name updated")("Updated Feature Set")(upd.name);
  TestValidator.equals("description updated")("Updated description.")(upd.description);
  TestValidator.equals("schema updated")('{"fields":[{"name":"f1","type":"float"},{"name":"f2","type":"int"}]}')(upd.schema);

  // 3. Fetch updated (no GET endpoint: use 'upd' output), changes checked above

  // 4. Update with invalid ID
  await TestValidator.httpError("update with invalid id")(404)(() =>
    api.functional.advancedAI.aiFeatureStore.putById(connection, {
      id: "00000000-0000-0000-0000-000000000000",
      body: {
        name: "Should Not Work",
      },
    }),
  );

  // 5. Update with bad payload (e.g., missing required format in schema field)
  await TestValidator.error("update with invalid data")(async () =>
    api.functional.advancedAI.aiFeatureStore.putById(connection, {
      id: original.id,
      body: {
        schema: "", // likely invalid: schema required non-empty string
      },
    }),
  );
}

