import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IAIDecisionLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IAIDecisionLog";
import { IPageIAIDecisionLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAIDecisionLog";

/**
 * E2E test for listing/searching AI decision logs with various filters and pagination.
 *
 * This test validates the AI decision log listing endpoint. It first creates several logs as test data,
 * then performs list/search queries to confirm correct filtering, result structure, pagination, and empty-list behavior.
 *
 * 1. Create multiple decision logs (differ by model version, rationale, entity, decision)
 * 2. List all without filter
 * 3. List by ai_model_version_id filter (should return logs with that version only)
 * 4. List by target_entity filter (e.g. 'order')
 * 5. List by rationale substring filter
 * 6. Paginate with limit=2, page=1, page=2
 * 7. List using a filter producing zero results (e.g. bogus entity)
 * 8. Validate structure, correctness, and empty result handling
 */
export async function test_api_ai_decision_log_list_and_search_filters(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Create test data (multiple AI decision logs with varied fields)
  const logs: IAIDecisionLog[] = [];
  const modelVersionIds = [typia.random<string>(), typia.random<string>()];
  const targetEntities = ["order", "product", "review"];

  for (let i = 0; i < 5; ++i) {
    // Vary fields for later filterability
    const modelVersionId = modelVersionIds[i % modelVersionIds.length];
    const targetEntity = targetEntities[i % targetEntities.length];
    const rationale = i % 2 === 0 ? `special-rationale-${i}` : "normal";

    const created: IAIDecisionLog = await api.functional.advancedAI.aiDecisionLogs.post(
      connection,
      {
        body: {
          ai_model_version_id: modelVersionId,
          target_entity: targetEntity,
          target_entity_id: typia.random<string>(),
          decision: i % 2 === 0 ? "recommend" : "block",
          input_snapshot: JSON.stringify({ test: i }),
          // only attach rationale on some
          rationale: rationale,
        } satisfies IAIDecisionLog.ICreate,
      },
    );
    typia.assert(created);
    logs.push(created);
  }

  // Step 2: List all (no filter) - Should include all test logs
  let response: IPageIAIDecisionLog = await api.functional.advancedAI.aiDecisionLogs.patch(
    connection, { body: {} }
  );
  typia.assert(response);
  TestValidator.equals("all logs are present")(
    logs.map(l => l.id).sort()
  )(
    response.data.map(l => l.id).filter(x => logs.find(l => l.id === x)).sort()
  );

  // Step 3: Filter by ai_model_version_id
  response = await api.functional.advancedAI.aiDecisionLogs.patch(
    connection, { body: { ai_model_version_id: modelVersionIds[0] } },
  );
  typia.assert(response);
  for (const log of response.data) {
    TestValidator.equals("model version filtered correctly")(modelVersionIds[0])(log.ai_model_version_id);
  }

  // Step 4: Filter by target_entity
  response = await api.functional.advancedAI.aiDecisionLogs.patch(
    connection, { body: { target_entity: "order" } },
  );
  typia.assert(response);
  for (const log of response.data) {
    TestValidator.equals("entity filtered correctly")("order")(log.target_entity);
  }

  // Step 5: Filter by rationale substring
  response = await api.functional.advancedAI.aiDecisionLogs.patch(
    connection, { body: { rationale: "special-rationale" } },
  );
  typia.assert(response);
  for (const log of response.data) {
    TestValidator.equals("rationale filtered correctly")(
      true
    )(
      typeof log.rationale === "string" && log.rationale.includes("special-rationale")
    );
  }

  // Step 6: Pagination test (limit=2)
  response = await api.functional.advancedAI.aiDecisionLogs.patch(
    connection, { body: { limit: 2, page: 1 } },
  );
  typia.assert(response);
  TestValidator.equals("pagination, limit=2")(
    2
  )(response.data.length);
  const responsePage2 = await api.functional.advancedAI.aiDecisionLogs.patch(
    connection, { body: { limit: 2, page: 2 } },
  );
  typia.assert(responsePage2);
  TestValidator.equals("pagination page 2")(
    2
  )(responsePage2.pagination.current <= responsePage2.pagination.pages ? responsePage2.data.length : 0);

  // Step 7: Filter producing zero results (bogus entity)
  response = await api.functional.advancedAI.aiDecisionLogs.patch(
    connection, { body: { target_entity: "bogus-entity" } },
  );
  typia.assert(response);
  TestValidator.equals("empty result for bogus entity")(0)(response.data.length);
}

