 */
export async function test_api_ai_decision_log_update_with_valid_and_invalid_inputs(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a valid AI decision log
  const log: IAIDecisionLog = await api.functional.advancedAI.aiDecisionLogs.post(connection, {
    body: {
      ai_model_version_id: "d290f1ee-6c54-4b01-90e6-d701748f0851",
