import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IAiFeedback } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiFeedback";

/**
 * Validate the creation of AI feedback events.
 *
 * Scenario Background:
 * Test the creation endpoint for AI feedback records, crucial for model retraining loop, explainability, compliance, and audit purposes. The endpoint requires strict adherence to required fields and robust data validation (decision log linkage, type, value, metadata, etc.) per regulatory & evidence requirements.
 *
 * Test Steps:
 * 1. Attempt to create a new AI feedback record with all mandatory attributes; expect successful creation and persistence of the feedback event.
 * 2. Attempt to create an AI feedback record with missing required fields (e.g., missing decision_log_id or feedback_type) or invalid data types/values. Expect validation errors, and verify error responses.
 */
export async function test_api_ai_feedback_create(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create with all required (and some optional) fields
  const validBody: IAiFeedback.ICreate = {
    decision_log_id: "7f59c372-12c3-4b27-99ef-8ca101542e8e",
    user_id: "0d2e3967-2b0b-4152-bd6a-fec586b2ed38",
    feedback_type: "accepted",
    feedback_value: "positive",
    metadata: JSON.stringify({ device: "web", reason: "explicit user click" }),
  };
  const created: IAiFeedback = await api.functional.advancedAI.aiFeedback.post(
    connection,
    {
      body: validBody,
    },
  );
  typia.assert(created);
  TestValidator.equals("decision_log_id")(validBody.decision_log_id)(created.decision_log_id);
  TestValidator.equals("feedback_type")(validBody.feedback_type)(created.feedback_type);
  TestValidator.equals("feedback_value")(validBody.feedback_value)(created.feedback_value);
  TestValidator.equals("user_id")(validBody.user_id)(created.user_id);
  // created_at and id should be present and valid
  typia.assert(created.id);
  typia.assert(created.created_at);

  // 2. Try creation with missing required field: feedback_type
  const missingType: IAiFeedback.ICreate = {
    ...validBody,
    feedback_type: undefined as any,
  };
  await TestValidator.httpError("missing feedback_type")(422)(() =>
    api.functional.advancedAI.aiFeedback.post(connection, { body: missingType }),
  );

  // 3. Try creation with missing required field: decision_log_id
  const missingDecisionLogID: IAiFeedback.ICreate = {
    ...validBody,
    decision_log_id: undefined as any,
  };
  await TestValidator.httpError("missing decision_log_id")(422)(() =>
    api.functional.advancedAI.aiFeedback.post(connection, { body: missingDecisionLogID }),
  );

  // 4. Invalid feedback_type (type safety violation)
  const invalidType: IAiFeedback.ICreate = {
    ...validBody,
    feedback_type: 1234 as any,
  };
  await TestValidator.httpError("invalid feedback_type type")(422)(() =>
    api.functional.advancedAI.aiFeedback.post(connection, { body: invalidType }),
  );

  // 5. Invalid UUID format for decision_log_id
  const invalidUUID: IAiFeedback.ICreate = {
    ...validBody,
    decision_log_id: "not-a-uuid" as any,
  };
  await TestValidator.httpError("invalid decision_log_id uuid")(422)(() =>
    api.functional.advancedAI.aiFeedback.post(connection, { body: invalidUUID }),
  );
}

