import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IPageISecurityEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageISecurityEvent";
import { ISecurityEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/ISecurityEvent";

/**
 * Test listing and filtering of security event logs by type, time, and other filters, including pagination and zero-results conditions.
 *
 * Validates correct function of filtering and pagination for security event logs in SecurityCompliance module:
 *
 * 1. Create several distinct security events with various event_type, user/session context, and created_at timestamps.
 * 2. Query by event_type, by user/session, and by time window; validate only matching records are returned.
 * 3. Query with pagination settings (page/limit > 1), check returned item count, and verify pagination metadata.
 * 4. Query with filter guaranteed (or extremely likely) to match zero records; assert empty results and correct pagination.
 * 5. Assert all returned event records match the filter and paginated response matches expectations.
 */
export async function test_api_security_event_list_and_filter(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Create several security events
  const now = new Date();
  const events: ISecurityEvent[] = [];
  for (let i = 0; i < 7; ++i) {
    const eventType = `custom_event_type_${i%3}`;
    const userId = `00000000-0000-0000-0000-00000000000${i%2}`;
    const sessionId = `11111111-1111-1111-1111-11111111111${i%3}`;
    const createdAt = new Date(+now - i * 60_1000).toISOString(); // spaced 1 min apart
    const created: ISecurityEvent = await api.functional.securityCompliance.securityEvent.post(connection, {
      body: {
        user_id: userId,
        session_id: sessionId,
        api_client_id: null,
        event_type: eventType,
        event_details: JSON.stringify({ foo: i }),
        created_at: createdAt,
      } satisfies ISecurityEvent.ICreate,
    });
    typia.assert(created);
    events.push(created);
  }

  // Step 2: Query and filter by event_type
  {
    const eventType = events[3].event_type;
    const out: IPageISecurityEvent = await api.functional.securityCompliance.securityEvent.patch(connection, {
      body: { event_type: eventType } satisfies ISecurityEvent.IRequest,
    });
    typia.assert(out);
    TestValidator.equals("all rows match event_type")(
      true,
    )(out.data.every(ev => ev.event_type === eventType));
  }

  // Step 2b: Filter by user_id
  {
    const userId = events[1].user_id!;
    const out: IPageISecurityEvent = await api.functional.securityCompliance.securityEvent.patch(connection, {
      body: { user_id: userId } satisfies ISecurityEvent.IRequest,
    });
    typia.assert(out);
    TestValidator.equals("all rows match user_id")(
      true,
    )(out.data.every(ev => ev.user_id === userId));
  }

  // Step 2c: Filter by session_id
  {
    const sessionId = events[4].session_id!;
    const out: IPageISecurityEvent = await api.functional.securityCompliance.securityEvent.patch(connection, {
      body: { session_id: sessionId } satisfies ISecurityEvent.IRequest,
    });
    typia.assert(out);
    TestValidator.equals("all rows match session_id")(
      true,
    )(out.data.every(ev => ev.session_id === sessionId));
  }

  // Step 2d: Filter by created_at time window (between events[2] and events[5])
  {
    // Get time window spanning 3 events
    const fromTime = events[5].created_at;
    const toTime = events[2].created_at;
    const out: IPageISecurityEvent = await api.functional.securityCompliance.securityEvent.patch(connection, {
      body: {
        from_time: fromTime,
        to_time: toTime,
      } satisfies ISecurityEvent.IRequest,
    });
    typia.assert(out);
    TestValidator.equals("all rows in time window")(
      true,
    )(out.data.every(ev =>
      ev.created_at >= fromTime && ev.created_at <= toTime
    ));
  }

  // Step 3: Pagination: limit to 2 per page, get page 2
  {
    const out: IPageISecurityEvent = await api.functional.securityCompliance.securityEvent.patch(connection, {
      body: { page: 2, limit: 2, sort_order: "asc" } satisfies ISecurityEvent.IRequest,
    });
    typia.assert(out);
    TestValidator.equals("pagination.page == 2")(2)(out.pagination.current);
    TestValidator.equals("pagination.limit == 2")(2)(out.pagination.limit);
    TestValidator.equals("correct data array size for limit=2")(
      Math.min(2, out.pagination.records - 2),
    )(out.data.length);
  }

  // Step 4: Zero-result query (future time window)
  {
    const futureFrom = new Date(+now + 10_000_000).toISOString();
    const out: IPageISecurityEvent = await api.functional.securityCompliance.securityEvent.patch(connection, {
      body: { from_time: futureFrom } satisfies ISecurityEvent.IRequest,
    });
    typia.assert(out);
    TestValidator.equals("no results in future window")(0)(out.data.length);
    TestValidator.equals("pagination.records==0")(0)(out.pagination.records);
  }
}

