import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IDeletionResult } from "@ORGANIZATION/PROJECT-api/lib/structures/IDeletionResult";
import { ISecurityEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/ISecurityEvent";

/**
 * Delete a security event log by valid ID and confirm removal.
 *
 * Also, try to delete again using the same ID (already deleted), or delete using a non-existent ID, and verify that the API responds with compliant error or an idempotent no-op.
 *
 * 1. Create a security event log and get its ID.
 * 2. Delete the security event by its ID and confirm it is removed via deletion status/result.
 * 3. Try to delete again by the same ID and verify error response or idempotent no-op (status).
 * 4. Try to delete a random/non-existent event ID, and check that a compliant error or not-found result occurs.
 */
export async function test_api_security_event_delete_and_idempotency(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a security event to ensure a real, valid ID is used
  const event: ISecurityEvent = await api.functional.securityCompliance.securityEvent.post(connection, {
    body: {
      event_type: "manual_test_delete",
      event_details: JSON.stringify({ reason: "E2E test valid delete" }),
    },
  } as any);
  typia.assert(event);

  // 2. Delete the security event by valid ID
  const deletion: IDeletionResult = await api.functional.securityCompliance.securityEvent.eraseById(connection, {
    id: event.id,
  });
  typia.assert(deletion);
  TestValidator.equals("deleted id")(event.id)(deletion.id);
  TestValidator.equals("delete status: deleted")("deleted")(deletion.status);

  // 3. Try to delete again (already deleted)
  await TestValidator.httpError("deleting already deleted event")([404, 410, 400, 409])(() =>
    api.functional.securityCompliance.securityEvent.eraseById(connection, {
      id: event.id,
    }),
  );

  // 4. Try to delete a non-existent random ID
  const uuid =
    event.id.length === 36
      ?
        event.id
          .split("")
          .map((c, i) => (i === 34 ? (c === "a" ? "b" : "a") : c))
          .join("")
      :
        event.id.slice(0, -2) + Math.random().toString().slice(-2);
  await TestValidator.httpError("deleting non-existent event id")([404, 410, 400, 409])(() =>
    api.functional.securityCompliance.securityEvent.eraseById(connection, {
      id: uuid as any,
    }),
  );
}

import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IPageIPermission } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIPermission";
import { IPermission } from "@ORGANIZATION/PROJECT-api/lib/structures/IPermission";

/**
 * List and filter permission records with various filters and pagination.
 *
 * This test validates the permission registry search function. It creates
diverse permission records, then runs filtered and paginated queries to
confirm correctness for normal and edge cases, such as:
 * - Filtering by code, description, or policy_id
 * - Pagination (pages/limit)
 * - Fuzzy and exact matches
 * - Empty result when filters yield no matches
 *
 * Steps:
 * 1. Create several permission records with distinguishable codes and descriptions
 * 2. List all permissions, confirm all created present
 * 3. Filter by code (exact): expect only matching permission
 * 4. Filter by description (partial): expect subset
 * 5. Filter by policy_id (if used): expect those records only
 * 6. Paginate (limit=2,page=1,2...): expect chunked results, totals correct
 * 7. Query with a filter that returns no records: expect empty result array, page metadata correct
 */
export async function test_api_permission_list_with_various_filters(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create several permission records
  const permsData = [
    { code: "order.list", description: "Order listing" },
    { code: "order.edit", description: "Order edit permission" },
    { code: "user.read", description: "User read" },
    { code: "user.write", description: "User write access" }
  ];
  const created: IPermission[] = [];
  for(const perm of permsData) {
    const out = await api.functional.securityCompliance.permission.post(connection, {
      body: {
        code: perm.code,
        description: perm.description,
      } satisfies IPermission.ICreate,
    });
    typia.assert(out);
    created.push(out);
  }

  // 2. List all permissions, confirm all created are present
  const all: IPageIPermission = await api.functional.securityCompliance.permission.patch(connection, {
    body: {},
  });
  typia.assert(all);
  for(const perm of created) {
    const found = all.data.find(r => r.id === perm.id);
    TestValidator.equals(`permission ${perm.code} present`)(true)(!!found);
  }

  // 3. Filter by code (exact)
  const codeMatch = created[1]; // e.g. "order.edit"
  const listByCode: IPageIPermission = await api.functional.securityCompliance.permission.patch(connection, {
    body: { code: codeMatch.code },
  });
  typia.assert(listByCode);
  TestValidator.equals("listByCode size")(1)(listByCode.data.length);
  TestValidator.equals("listByCode match")(codeMatch.id)(listByCode.data[0].id);

  // 4. Filter by description (partial)
  const descQuery = "User";
  const byDesc: IPageIPermission = await api.functional.securityCompliance.permission.patch(connection, {
    body: { description: descQuery },
  });
  typia.assert(byDesc);
  TestValidator.equals("byDesc any")(true)(byDesc.data.length > 0);
  for(const perm of byDesc.data) {
    TestValidator.equals("desc contains query")(true)(perm.description.includes(descQuery));
  }

  // 5. (Optional) Filter by policy_id (create one with policy for this)
  // For this, create a permission with a unique policy_id
  const policyId = "00000000-0000-4000-a000-000000000001";
  const permPolicy = await api.functional.securityCompliance.permission.post(connection, {
    body: { code: "special.policy", description: "Special Policy", policy_id: policyId } satisfies IPermission.ICreate,
  });
  typia.assert(permPolicy);
  const byPolicy: IPageIPermission = await api.functional.securityCompliance.permission.patch(connection, {
    body: { policy_id: policyId },
  });
  typia.assert(byPolicy);
  TestValidator.equals("byPolicy only policy perms")(true)(byPolicy.data.every(p => p.policy_id === policyId));
  TestValidator.equals("byPolicy contains ours")(true)(!!byPolicy.data.find(p => p.id === permPolicy.id));

  // 6. Pagination (limit=2)
  const paged1: IPageIPermission = await api.functional.securityCompliance.permission.patch(connection, {
    body: { limit: 2, page: 1 },
  });
  typia.assert(paged1);
  TestValidator.equals("paged1 size")(2)(paged1.data.length);
  const paged2: IPageIPermission = await api.functional.securityCompliance.permission.patch(connection, {
    body: { limit: 2, page: 2 },
  });
  typia.assert(paged2);
  TestValidator.equals("paged2 overlap")(false)(
    paged1.data.every(r1 => paged2.data.some(r2 => r2.id === r1.id))
  );

  // 7. No results
  const noRes: IPageIPermission = await api.functional.securityCompliance.permission.patch(connection, {
    body: { code: "does.not.exist" },
  });
  typia.assert(noRes);
  TestValidator.equals("noRes empty")(0)(noRes.data.length);
}

import { TestValidator } from "@nestia/e2e";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IPermission } from "@ORGANIZATION/PROJECT-api/lib/structures/IPermission";

/**
 * Retrieve a permission record by valid ID and confirm accurate detail.
 * Also verify error handling for invalid/non-existent ID.
 *
 * 1. Create a permission (POST /securityCompliance/permission) to obtain a valid ID.
 * 2. Retrieve the created permission by its ID (GET /securityCompliance/permission/{id}) and confirm all fields match.
 * 3. Attempt to retrieve permission by an invalid/non-existent ID and confirm proper error handling (404 or appropriate error).
 */
export async function test_api_permission_get_by_valid_and_invalid_id(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a permission
  const permissionCreate: IPermission.ICreate = {
    code: `order.edit.${Date.now()}`,
    description: "Ability to edit order records.",
  };
  const created: IPermission = await api.functional.securityCompliance.permission.post(
    connection,
    { body: permissionCreate },
  );
  typia.assert(created);

  // 2. Retrieve the created permission by ID and validate fields
  const retrieved: IPermission = await api.functional.securityCompliance.permission.getById(
    connection,
    { id: created.id },
  );
  typia.assert(retrieved);
  TestValidator.equals("id should match")(created.id)(retrieved.id);
  TestValidator.equals("code should match")(created.code)(retrieved.code);
  TestValidator.equals("description should match")(created.description)(retrieved.description);
  // Policy ID, created_at are checked for type assertion

  // 3. Attempt to retrieve with invalid/non-existent ID and ensure error
  const randomId: string & tags.Format<"uuid"> = typia.random<string & tags.Format<"uuid">>();
  if (randomId !== created.id) {
    await TestValidator.httpError("should fail for non-existent ID")(404)(() =>
      api.functional.securityCompliance.permission.getById(connection, { id: randomId })
    );
  } else {
    // Should never reach: regenerate randomId if a rare collision
    await TestValidator.httpError("should fail for wrong ID")(404)(() =>
      api.functional.securityCompliance.permission.getById(connection, { id: typia.random<string & tags.Format<"uuid">>() })
    );
  }
}

