    await api.functional.securityCompliance.permission.post(connection, {
      body: createInput,
    });
  typia.assert(permission);

  // 2. Delete the permission by ID
  const deleted: IPermission = await api.functional.securityCompliance.permission.eraseById(connection, {
    id: permission.id,
  });
  typia.assert(deleted);
  TestValidator.equals("deleted permission ID")(permission.id)(deleted.id);

  // 3. Try to delete again (should fail or be idempotent)
  await TestValidator.httpError("deleting already deleted permission should fail or return not found")(404)(() =>
    api.functional.securityCompliance.permission.eraseById(connection, {
      id: permission.id,
    }),
  );

