import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IPageIUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIUser";
import typia from "typia";

export async function test_list_users_with_various_filters_and_pagination(connection: api.IConnection): Promise<void> {
  // 1. Default request (no filter)
  const resultDefault = await api.functional.core.users.patch(connection, { body: {} });
  typia.assert<IPageIUser.ISummary>(resultDefault);
  TestValidator.predicate("default result is non-empty")(() => resultDefault.data.length >= 0);
  TestValidator.predicate("pagination correctness")(
    () => resultDefault.pagination.current === 1 || typeof resultDefault.pagination.current === "number"
  );

  // 2. Pagination (custom page/limit)
  const pagReq: IUser.IRequest = { page: 2, limit: 3 };
  const resultPage = await api.functional.core.users.patch(connection, { body: pagReq });
  typia.assert<IPageIUser.ISummary>(resultPage);
  TestValidator.equals("requested page number")<number>(pagReq.page!)(resultPage.pagination.current);
  TestValidator.equals("requested limit")<number>(pagReq.limit!)(resultPage.pagination.limit);

  // 3. Try to fetch a known invalid page (e.g., page far beyond pageCount)
  const invalidPage = 99999;
  const resultInvalidPage = await api.functional.core.users.patch(connection, { body: { page: invalidPage } });
  typia.assert<IPageIUser.ISummary>(resultInvalidPage);
  TestValidator.equals("empty on unavailable page")<number>(0)(resultInvalidPage.data.length);

  // 4. Search filter (use a clearly gibberish string for empty result)
  const gibberishSearch = "no-user-in-db-xyz123";
  const resultEmpty = await api.functional.core.users.patch(connection, { body: { search: gibberishSearch } });
  typia.assert<IPageIUser.ISummary>(resultEmpty);
  TestValidator.equals("no results on gibberish search")<number>(0)(resultEmpty.data.length);

  // 5. If any users exist, pick a role and status from data and filter by them
  const firstUser = resultDefault.data[0];
  if (firstUser) {
    const byRole = await api.functional.core.users.patch(connection, {
      body: { role: firstUser.role.id },
    });
    typia.assert<IPageIUser.ISummary>(byRole);
    TestValidator.predicate("all users have matching role")(() => byRole.data.every(u => u.role.id === firstUser.role.id));

    const byStatus = await api.functional.core.users.patch(connection, {
      body: { status: firstUser.status },
    });
    typia.assert<IPageIUser.ISummary>(byStatus);
    TestValidator.predicate("all users have matching status")(() => byStatus.data.every(u => u.status === firstUser.status));

    // Search by username (partial, case-insensitive)
    const usernameFragment = firstUser.username.slice(0, Math.max(1, Math.floor(firstUser.username.length / 2)));
    const bySearch = await api.functional.core.users.patch(connection, {
      body: { search: usernameFragment },
    });
    typia.assert<IPageIUser.ISummary>(bySearch);
    TestValidator.predicate("some users match username search")(() => bySearch.data.some(u => u.username.includes(usernameFragment)));
  }

  // 6. Invalid pagination (negative page/limit)
  await TestValidator.httpError("negative page") (400)(async () => {
    await api.functional.core.users.patch(connection, { body: { page: -1 } });
  });
  await TestValidator.httpError("negative limit") (400)(async () => {
    await api.functional.core.users.patch(connection, { body: { limit: -5 } });
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IUserRole } from "@ORGANIZATION/PROJECT-api/lib/structures/IUserRole";
import { IPageIUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIUser";
import typia from "typia";

export async function test_search_users_with_role_and_keyword_filters(
  connection: api.IConnection,
): Promise<void> {
  // 1. Preparation: Create two user roles
  const roleA = await api.functional.core.userRoles.post(connection, {
    body: { name: "RoleA", description: "Test Role A" },
  });
  typia.assert<IUserRole>(roleA);
  const roleB = await api.functional.core.userRoles.post(connection, {
    body: { name: "RoleB", description: "Test Role B" },
  });
  typia.assert<IUserRole>(roleB);

  // 2. Preparation: Create three users with above roles.
  const user1 = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: roleA.id,
      username: "alice_test",
      email: `alice_${Math.random().toString(36).substring(2, 6)}@example.com`,
      bio: null,
      password: "Password123!",
      status: "active",
    },
  });
  typia.assert<IUser>(user1);

  const user2 = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: roleB.id,
      username: "bob_admin",
      email: `bob_${Math.random().toString(36).substring(2, 6)}@example.com`,
      bio: null,
      password: "Password123!",
      status: "active",
    },
  });
  typia.assert<IUser>(user2);

  const user3 = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: roleA.id,
      username: "carol_test",
      email: `carol_${Math.random().toString(36).substring(2, 6)}@example.com`,
      bio: null,
      password: "Password123!",
      status: "active",
    },
  });
  typia.assert<IUser>(user3);

  // Helper for easy mapping to summary objects
  const toSummary = (u: IUser): IUser.ISummary => ({
    id: u.id,
    username: u.username,
    bio: u.bio,
    role: { id: u.role.id, name: u.role.name },
    status: u.status,
  });

  // 3. Test search by role only (RoleA)
  const byRoleA = await api.functional.core.users.patch(connection, {
    body: { role: roleA.id },
  });
  typia.assert<IPageIUser.ISummary>(byRoleA);
  // should include user1 and user3
  TestValidator.predicate("RoleA filter contains correct users")(
    () => byRoleA.data.some(u => u.id === user1.id) && byRoleA.data.some(u => u.id === user3.id) &&
    byRoleA.data.every(u => u.role.id === roleA.id)
  );

  // 4. Test search by partial username ('test')
  const byTest = await api.functional.core.users.patch(connection, {
    body: { search: "test" },
  });
  typia.assert<IPageIUser.ISummary>(byTest);
  // Usernames with "test" => user1 and user3
  TestValidator.equals("Partial username filter")(
    [toSummary(user1), toSummary(user3)]
  )(
    byTest.data.filter(u => u.username.endsWith("_test") || u.username.startsWith("carol"))
  );

  // 5. Search with both role and username filter (roleA + 'carol')
  const byRoleAAndCarol = await api.functional.core.users.patch(connection, {
    body: { role: roleA.id, search: "carol" },
  });
  typia.assert<IPageIUser.ISummary>(byRoleAAndCarol);
  TestValidator.equals("RoleA+carol filter")(
    [toSummary(user3)]
  )(
    byRoleAAndCarol.data.filter(u => u.username === "carol_test")
  );

  // 6. Edge: Non-existent role id
  const byFakeRole = await api.functional.core.users.patch(connection, {
    body: { role: "11111111-1111-1111-1111-111111111111" },
  });
  typia.assert<IPageIUser.ISummary>(byFakeRole);
  TestValidator.equals("Fake role returns no results")(
    [] as IUser.ISummary[]
  )(byFakeRole.data);

  // 7. Edge: Non-existent search string
  const byNonsense = await api.functional.core.users.patch(connection, {
    body: { search: "zzzzzzzz_wrong" },
  });
  typia.assert<IPageIUser.ISummary>(byNonsense);
  TestValidator.equals("Nonsense search returns no results")(
    [] as IUser.ISummary[]
  )(byNonsense.data);
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";

import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";

export async function test_retrieve_user_profile_by_id_success_and_not_found(connection: api.IConnection): Promise<void> {
  // Step 1: Register a new user
  const userInput: IUser.ICreate = {
    user_role_id: typia.random<string & tags.Format<"uuid">>(),
    username: `testuser_${Math.random().toString(36).slice(2)}`,
    email: `testuser_${Math.random().toString(36).slice(2)}@example.com`,
    bio: "Test user for retrieval",
    password: "StrongPassword123!",
    status: "active",
  };
  const created: IUser = await api.functional.core.users.post(connection, {
    body: userInput,
  });
  typia.assert<IUser>(created);
  TestValidator.equals("username matches")(userInput.username)(created.username);
  TestValidator.equals("email matches")(userInput.email)(created.email);
  TestValidator.equals("user_role_id matches")(userInput.user_role_id)(created.user_role_id);

  // Step 2: Retrieve the newly created user by ID (should succeed)
  const retrieved: IUser = await api.functional.core.users.getById(connection, {
    id: created.id,
  });
  typia.assert<IUser>(retrieved);
  TestValidator.equals("user id matches")(created.id)(retrieved.id);
  TestValidator.equals("username matches")(created.username)(retrieved.username);
  TestValidator.equals("email matches")(created.email)(retrieved.email);

  // Step 3: Attempt to retrieve a user by an invalid/deleted ID (should 404)
  const bogusUserId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("should be 404 when user not found")(404)(async () => {
    await api.functional.core.users.getById(connection, { id: bogusUserId });
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IUserRole } from "@ORGANIZATION/PROJECT-api/lib/structures/IUserRole";
import typia from "typia";

export async function test_register_new_user_edge_and_success_cases(connection: api.IConnection): Promise<void> {
  // Step 1: Create a user role for registration
  const userRoleInput: IUserRole.ICreate = {
    name: `member_${Date.now()}`,
    description: "Basic member role for testing user registration."
  };

  const role = await api.functional.core.userRoles.post(connection, { body: userRoleInput });
  typia.assert<IUserRole>(role);

  // Step 2: Register a new user with all valid fields
  const validUser: IUser.ICreate = {
    user_role_id: role.id,
    username: `user_${Date.now()}`,
    email: `user_${Date.now()}@test.com`,
    bio: "Test user bio.",
    password: "super-secret-password",
    status: "active"
  };
  const user = await api.functional.core.users.post(connection, { body: validUser });
  typia.assert<IUser>(user);
  TestValidator.equals("New user username matches input")<string>(validUser.username)(user.username);
  TestValidator.equals("User role matches input")<string>(validUser.user_role_id)(user.user_role_id);
  TestValidator.equals("User status is active")<string>("active")(user.status);
  TestValidator.equals("User email matches input")<string>(validUser.email)(user.email);
  // Email is returned for the user (not hidden)

  // Step 3a: Missing required field (username)
  const missingUsername: IUser.ICreate = {
    user_role_id: role.id,
    username: "",
    email: `otheruser_${Date.now()}@test.com`,
    password: "pw1",
    status: "active"
  };
  await TestValidator.httpError("Missing username gives 400") (400)(async () =>
    api.functional.core.users.post(connection, { body: missingUsername })
  );

  // Step 3b: Missing required field (email)
  const missingEmail: IUser.ICreate = {
    user_role_id: role.id,
    username: `missingEmail_${Date.now()}`,
    email: "",
    password: "pw1",
    status: "active"
  };
  await TestValidator.httpError("Missing email gives 400") (400)(async () =>
    api.functional.core.users.post(connection, { body: missingEmail })
  );

  // Step 3c: Missing required field (password)
  const missingPassword: IUser.ICreate = {
    user_role_id: role.id,
    username: `missingPassword_${Date.now()}`,
    email: `missingpw_${Date.now()}@test.com`,
    password: "",
    status: "active"
  };
  await TestValidator.httpError("Missing password gives 400") (400)(async () =>
    api.functional.core.users.post(connection, { body: missingPassword })
  );

  // Step 4: Invalid email format
  const invalidEmail: IUser.ICreate = {
    user_role_id: role.id,
    username: `invalidEmail_${Date.now()}`,
    email: "notanemail",
    password: "pw1",
    status: "active"
  };
  await TestValidator.httpError("Invalid email rejected") (400)(async () =>
    api.functional.core.users.post(connection, { body: invalidEmail })
  );

  // Step 5: Duplicate registration (username and email)
  const duplicateUser: IUser.ICreate = {
    ...validUser,
    username: validUser.username, // same username
    email: validUser.email,       // same email
  };
  await TestValidator.httpError("Duplicate username/email gives 409/400") (409, 400)(async () =>
    api.functional.core.users.post(connection, { body: duplicateUser })
  );

  // Step 6: Partial duplicate (same username, diff email)
  const partialDup1: IUser.ICreate = {
    ...validUser,
    email: `unique_${Date.now()}@test.com`,
  };
  await TestValidator.httpError("Duplicate username (diff email)")(409,400)(async () =>
    api.functional.core.users.post(connection, { body: partialDup1 })
  );

  // Step 7: Partial duplicate (same email, diff username)
  const partialDup2: IUser.ICreate = {
    ...validUser,
    username: `diffuser_${Date.now()}`,
  };
  await TestValidator.httpError("Duplicate email (diff username)")(409,400)(async () =>
    api.functional.core.users.post(connection, { body: partialDup2 })
  );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IUserRole } from "@ORGANIZATION/PROJECT-api/lib/structures/IUserRole";
import typia, { tags } from "typia";

export async function test_update_user_profile_with_valid_and_invalid_conditions(connection: api.IConnection): Promise<void> {
  // Step 1: Create two distinct user roles
  const roleA: IUserRole = await api.functional.core.userRoles.post(connection, {
    body: { name: "memberA_" + Date.now(), description: "Standard member role A" },
  });
  typia.assert(roleA);
  const roleB: IUserRole = await api.functional.core.userRoles.post(connection, {
    body: { name: "memberB_" + Date.now(), description: "Standard member role B" },
  });
  typia.assert(roleB);

  // Step 2: Register two users with distinct emails and usernames
  const password = "Passw0rd!";
  const userAInput: IUser.ICreate = {
    user_role_id: roleA.id,
    username: "user_a_" + Date.now(),
    email: "usera_" + Date.now() + "@test.com",
    bio: "User A bio",
    password,
    status: "active",
  };
  const userBInput: IUser.ICreate = {
    user_role_id: roleB.id,
    username: "user_b_" + Date.now(),
    email: "userb_" + Date.now() + "@test.com",
    bio: "User B bio",
    password,
    status: "active",
  };
  const userA: IUser = await api.functional.core.users.post(connection, { body: userAInput });
  typia.assert(userA);
  const userB: IUser = await api.functional.core.users.post(connection, { body: userBInput });
  typia.assert(userB);

  // Step 3: Update userA's profile
  const newEmail = "updated_" + Date.now() + "@example.com";
  const newUsername = "updated_user_a_" + Date.now();
  const newBio = "Updated bio for user A";
  const newStatus = "suspended";
  const updateBody: IUser.IUpdate = {
    email: newEmail,
    username: newUsername,
    user_role_id: roleB.id,
    bio: newBio,
    status: newStatus,
  };
  const updatedUserA: IUser = await api.functional.core.users.putById(connection, {
    id: userA.id,
    body: updateBody,
  });
  typia.assert(updatedUserA);
  // Validate updates persisted
  TestValidator.equals("updated email")(newEmail)(updatedUserA.email);
  TestValidator.equals("updated username")(newUsername)(updatedUserA.username);
  TestValidator.equals("updated role")(roleB.id)(updatedUserA.user_role_id);
  TestValidator.equals("updated bio")(newBio)(updatedUserA.bio);
  TestValidator.equals("updated status")(newStatus)(updatedUserA.status);

  // Step 4: Attempt to update userA's email to userB's email (should fail/conflict)
  await TestValidator.httpError("duplicate email")(409)(async () => {
    await api.functional.core.users.putById(connection, {
      id: userA.id,
      body: { email: userB.email },
    });
  });

  // Step 5: Attempt to update userA's username to userB's username (should fail/conflict)
  await TestValidator.httpError("duplicate username")(409)(async () => {
    await api.functional.core.users.putById(connection, {
      id: userA.id,
      body: { username: userB.username },
    });
  });

  // Step 6: Update non-existent user (random UUID)
  const randomNonexistentId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("not found or deleted user")(404)(async () => {
    await api.functional.core.users.putById(connection, {
      id: randomNonexistentId,
      body: { email: "nonexistent_" + Date.now() + "@test.com" },
    });
  });

  // Step 7: Invalid email format
  await TestValidator.httpError("email format invalid")(400)(async () => {
    await api.functional.core.users.putById(connection, {
      id: userA.id,
      body: { email: "not-an-email" },
    });
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import typia, { tags } from "typia";

export async function test_delete_user_soft_and_hard_and_not_found(
  connection: api.IConnection,
): Promise<void> {
  // 1. Register a user
  const userInput: IUser.ICreate = typia.random<IUser.ICreate>();
  const createdUser: IUser = await api.functional.core.users.post(connection, {
    body: userInput,
  });
  typia.assert<IUser>(createdUser);

  // 2. Soft-delete the user
  const deleteResp: IUser.IDeleteResponse = await api.functional.core.users.eraseById(connection, {
    id: createdUser.id,
  });
  typia.assert<IUser.IDeleteResponse>(deleteResp);
  TestValidator.equals("delete response user id")<string>(createdUser.id)(deleteResp.id!);
  TestValidator.predicate("delete response status string")(typeof deleteResp.status === "string");

  // 3. Try deleting same user again (should get 404)
  await TestValidator.httpError("delete already deleted user should 404")(404)(async () => {
    await api.functional.core.users.eraseById(connection, {
      id: createdUser.id,
    });
  });

  // 4. Try deleting a non-existent user (should get 404)
  const randomUuid: string & tags.Format<"uuid"> = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("delete non-existent user should 404")(404)(async () => {
    await api.functional.core.users.eraseById(connection, {
      id: randomUuid,
    });
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IUserRole } from "@ORGANIZATION/PROJECT-api/lib/structures/IUserRole";
import { IPageIUserRole } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIUserRole";

export async function test_list_user_roles_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create diverse user roles
  const rolesToCreate: IUserRole.ICreate[] = [
    { name: "member", description: "Basic board member who can post and comment" },
    { name: "moderator", description: "Can moderate content and manage users" },
    { name: "editor", description: "Edits content but no moderation rights" },
    { name: "guest", description: "Read-only access, cannot post" },
    { name: "superadmin", description: "All board privileges and system settings" },
  ];
  const createdRoles: IUserRole[] = [];
  for (const roleInput of rolesToCreate) {
    const created = await api.functional.core.userRoles.post(connection, { body: roleInput });
    typia.assert<IUserRole>(created);
    createdRoles.push(created);
  }

  // 2. List all roles, confirm all created present
  const allListResp = await api.functional.core.userRoles.patch(connection, { body: {} });
  typia.assert<IPageIUserRole>(allListResp);
  const allIds = createdRoles.map(r => r.id);
  const listedIds = allListResp.data.map(r => r.id);
  // All created IDs should be present
  for (const id of allIds) {
    TestValidator.predicate(`ID ${id} listed`)(() => listedIds.includes(id));
  }
  TestValidator.predicate("pagination count >= created count")(
    () => allListResp.pagination.records >= createdRoles.length,
  );

  // 3. Filter by specific role name
  const targetName = createdRoles[2].name; // 'editor'
  const filterByNameResp = await api.functional.core.userRoles.patch(connection, {
    body: { search: targetName },
  });
  typia.assert<IPageIUserRole>(filterByNameResp);
  // Every result must have the target name substring, in name or description
  for (const role of filterByNameResp.data) {
    TestValidator.predicate("role matches name filter")(
      () => role.name.includes(targetName) || role.description.includes(targetName),
    );
  }
  // Should include at least the specifically named one
  TestValidator.predicate("name filter returns at least one match")(
    () => filterByNameResp.data.length > 0,
  );

  // 4. Filter by description substring
  const descSearch = "moderate";
  const filterByDescResp = await api.functional.core.userRoles.patch(connection, {
    body: { search: descSearch },
  });
  typia.assert<IPageIUserRole>(filterByDescResp);
  for (const role of filterByDescResp.data) {
    TestValidator.predicate("role matches desc filter")(
      () => role.name.includes(descSearch) || role.description.includes(descSearch),
    );
  }
  TestValidator.predicate("desc filter returns >=1 match")(
    () => filterByDescResp.data.length > 0,
  );

  // 5. Test pagination (limit=2, page=1; page=2)
  const limit = 2;
  const page1Resp = await api.functional.core.userRoles.patch(connection, {
    body: { limit, page: 1 },
  });
  typia.assert<IPageIUserRole>(page1Resp);
  TestValidator.predicate("page 1 limit matches")(
    () => page1Resp.data.length <= limit,
  );
  TestValidator.equals("page 1 current page")<number>(1)(page1Resp.pagination.current);
  // Page 2
  const page2Resp = await api.functional.core.userRoles.patch(connection, {
    body: { limit, page: 2 },
  });
  typia.assert<IPageIUserRole>(page2Resp);
  TestValidator.predicate("page 2 limit matches")(
    () => page2Resp.data.length <= limit,
  );
  TestValidator.equals("page 2 current page")<number>(2)(page2Resp.pagination.current);
  // Make sure different IDs between pages if enough records
  if (page1Resp.data.length && page2Resp.data.length) {
    const ids1 = page1Resp.data.map(r => r.id);
    const ids2 = page2Resp.data.map(r => r.id);
    TestValidator.predicate("page 1 and 2 data differ")(() => ids1.some(id => !ids2.includes(id)));
  }

  // 6. Negative: search for non-existent
  const nonExistent = "no_such_role_" + Date.now();
  const negativeResp = await api.functional.core.userRoles.patch(connection, {
    body: { search: nonExistent },
  });
  typia.assert<IPageIUserRole>(negativeResp);
  TestValidator.equals("negative search yields empty array")<IUserRole[]>([])(negativeResp.data);
  TestValidator.equals("pagination zero records")<number>(0)(negativeResp.pagination.records);
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IUserRole } from "@ORGANIZATION/PROJECT-api/lib/structures/IUserRole";
import { IPageIUserRole } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIUserRole";

export async function test_list_user_roles_with_invalid_filter_parameters(
  connection: api.IConnection
): Promise<void> {
  // Array of invalid input cases
  const invalidInputs: IUserRole.IRequest[] = [
    { page: -1 },                // Negative page
    { page: 1.5 },               // Non-integer page
    { page: "a" as any },        // String instead of number
    { page: null as any },       // Null page
    { limit: 0 },                // Zero limit
    { limit: -10 },              // Negative limit
    { limit: "hundred" as any }, // String limit
    { limit: 99999999 },         // Excessively large limit
    { search: 12345 as any },    // Non-string search
    { search: "x".repeat(10001) } // Overly long search query
  ];

  for (const invalidBody of invalidInputs) {
    await TestValidator.httpError("invalid user role filter should fail")(400, 422)(
      async () => {
        await api.functional.core.userRoles.patch(connection, { body: invalidBody });
      }
    );
  }

  // Confirming a valid request still works
  const validBody: IUserRole.IRequest = { page: 1, limit: 5, search: "admin" };
  const result = await api.functional.core.userRoles.patch(connection, { body: validBody });
  typia.assert<IPageIUserRole>(result);
}

