export async function test_get_user_role_by_valid_and_invalid_id_with_auth_checks(connection: api.IConnection): Promise<void> {
  // 1. Create a new user role for positive retrieval test
  const roleInput: IUserRole.ICreate = {
    name: `test-role-${Date.now()}`,
    description: "E2E test role for GET /core/userRoles/{id} retrieval scenario."
  };
  const createdRole: IUserRole = await api.functional.core.userRoles.post(connection, { body: roleInput });
  typia.assert<IUserRole>(createdRole);

  // 2. Retrieve the created user role by valid ID
  const fetchedRole: IUserRole = await api.functional.core.userRoles.getById(connection, { id: createdRole.id });
  typia.assert<IUserRole>(fetchedRole);
  TestValidator.equals("Role ID matches")(createdRole.id)(fetchedRole.id);
  TestValidator.equals("Role name matches")(createdRole.name)(fetchedRole.name);
  TestValidator.equals("Role description matches")(createdRole.description)(fetchedRole.description);

