import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ICategory } from "@ORGANIZATION/PROJECT-api/lib/structures/ICategory";
import { IPageICategory } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICategory";
import typia from "typia";

export async function test_list_categories_with_pagination_and_filters(connection: api.IConnection): Promise<void> {
  // 1. Create multiple categories
  const categoriesToCreate: ICategory.ICreate[] = [
    {
      name: "Politics",
      description: "Everything about political systems.",
    },
    {
      name: "Economy",
      description: "International and local economics discussion.",
    },
    {
      name: "Sports",
      description: "All about sports and competitions.",
    },
    {
      name: "Tech",
      description: "Technology advancements and gadgets.",
    },
  ];
  const createdCategories: ICategory[] = [];
  for (const body of categoriesToCreate) {
    const category = await api.functional.core.categories.post(connection, { body });
    typia.assert<ICategory>(category);
    createdCategories.push(category);
  }

  // 2a. Default pagination (first page, limit 2)
  const reqPage1 = {
    page: 1,
    limit: 2,
  };
  const respPage1 = await api.functional.core.categories.patch(connection, { body: reqPage1 });
  typia.assert<IPageICategory>(respPage1);
  TestValidator.equals("limit for page 1")<number>(() => reqPage1.limit)(() => respPage1.pagination.limit);
  TestValidator.equals("current page 1")<number>(() => reqPage1.page)(() => respPage1.pagination.current);
  TestValidator.predicate("count is <= limit")(() => respPage1.data.length <= reqPage1.limit);

  // 2b. Filtering by name
  const filterByName = {
    page: 1,
    limit: 10,
    name: "Economy",
  };
  const respByName = await api.functional.core.categories.patch(connection, { body: filterByName });
  typia.assert<IPageICategory>(respByName);
  TestValidator.predicate("filter by name: only Economy present")(() => respByName.data.every(x => x.name === "Economy"));
  TestValidator.equals("filtered count against created data")<number>(() => createdCategories.filter(x => x.name === "Economy").length)(() => respByName.data.length);

  // 2c. Filtering by description (substring)
  const filterByDesc = {
    page: 1,
    limit: 10,
    description: "sports",
  };
  const respByDesc = await api.functional.core.categories.patch(connection, { body: filterByDesc });
  typia.assert<IPageICategory>(respByDesc);
  TestValidator.predicate("filter by description substring")(() => respByDesc.data.every(x => x.description.toLowerCase().includes("sports")));

  // 2d. Custom page/limit (offset)
  const reqPage2 = {
    page: 2,
    limit: 1,
  };
  const respPage2 = await api.functional.core.categories.patch(connection, { body: reqPage2 });
  typia.assert<IPageICategory>(respPage2);
  TestValidator.equals("page 2 limit")<number>(() => reqPage2.limit)(() => respPage2.pagination.limit);
  TestValidator.equals("page 2 current page")<number>(() => reqPage2.page)(() => respPage2.pagination.current);
  TestValidator.predicate("page 2 record exists or empty")(() => respPage2.data.length === 1 || respPage2.data.length === 0);

  // 3. Check pagination meta-maths
  const allPage = {
    page: 1,
    limit: 100,
  };
  const respAll = await api.functional.core.categories.patch(connection, { body: allPage });
  typia.assert<IPageICategory>(respAll);
  const meta = respAll.pagination;
  TestValidator.equals("meta total records math")<number>(() => respAll.data.length)(() => meta.records <= allPage.limit ? respAll.data.length : meta.records);
  TestValidator.equals("pages calculation")<number>(() => Math.ceil(meta.records / meta.limit))(() => meta.pages);

  // 4. Negative filter (nonexistent name)
  const filterNegative = {
    page: 1,
    limit: 10,
    name: "_not_exist_category_xyz_",
  };
  const respNeg = await api.functional.core.categories.patch(connection, { body: filterNegative });
  typia.assert<IPageICategory>(respNeg);
  TestValidator.equals("empty result when filtering by non-existent name")<number>(() => 0)(() => respNeg.data.length);
}

