import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { ICategory } from "@ORGANIZATION/PROJECT-api/lib/structures/ICategory";
import { IPost } from "@ORGANIZATION/PROJECT-api/lib/structures/IPost";
import { IPageIPost } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIPost";
import typia from "typia";

export async function test_list_posts_with_pagination_and_filters(connection: api.IConnection): Promise<void> {
  // 1. Create users
  const user1: IUser = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: typia.random<string>(),
      username: "user_alpha",
      email: `alpha_${Date.now()}@test.com`,
      bio: "Test Alpha Bio",
      password: "TestPassword1!",
      status: "active",
    },
  });
  const user2: IUser = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: user1.user_role_id,
      username: "user_beta",
      email: `beta_${Date.now()}@test.com`,
      bio: "Test Beta Bio",
      password: "TestPassword1!",
      status: "active",
    },
  });

  // 2. Create categories
  const category1: ICategory = await api.functional.core.categories.post(connection, {
    body: { name: `CategoryA_${Date.now()}`, description: "Test Category A" },
  });
  const category2: ICategory = await api.functional.core.categories.post(connection, {
    body: { name: `CategoryB_${Date.now()}`, description: "Test Category B" },
  });

  // 3. Create posts by different combinations
  const posts: IPost[] = [];
  // 2 for user1/category1
  posts.push(
    await api.functional.posts.posts.post(connection, {
      body: { category_id: category1.id, title: "Alpha1", content: "A1 Content", user_id: user1.id }
    })
  );
  posts.push(
    await api.functional.posts.posts.post(connection, {
      body: { category_id: category1.id, title: "Alpha2", content: "A2 Content", user_id: user1.id }
    })
  );
  // 1 for user1/category2
  posts.push(
    await api.functional.posts.posts.post(connection, {
      body: { category_id: category2.id, title: "Alpha3", content: "A3 Content", user_id: user1.id }
    })
  );
  // 1 for user2/category1
  posts.push(
    await api.functional.posts.posts.post(connection, {
      body: { category_id: category1.id, title: "Beta1", content: "B1 Content", user_id: user2.id }
    })
  );
  // 2 for user2/category2
  posts.push(
    await api.functional.posts.posts.post(connection, {
      body: { category_id: category2.id, title: "Beta2", content: "B2 Content", user_id: user2.id }
    })
  );
  posts.push(
    await api.functional.posts.posts.post(connection, {
      body: { category_id: category2.id, title: "Beta3", content: "B3 Content", user_id: user2.id }
    })
  );

  // Helper for assertion
  const pageEquals = (expected: number) => (page: IPageIPost) => {
    TestValidator.equals("pagination count")<number>(() => expected)(page.data.length);
    typia.assert<IPageIPost>(page);
  };

  // 4. List all posts, no filter
  const listAll = await api.functional.posts.posts.patch(connection, {
    body: { page: 1, limit: 100 },
  });
  pageEquals(posts.length)(listAll);

  // 5. Filter by author (user_id)
  for (const user of [user1, user2]) {
    const filtered = await api.functional.posts.posts.patch(connection, {
      body: { user_id: user.id, page: 1, limit: 100 },
    });
    typia.assert<IPageIPost>(filtered);
    for (const p of filtered.data) {
      TestValidator.equals("user_id matches")(user.id)(p.user_id);
    }
  }
  // 6. Filter by category (category_id)
  for (const category of [category1, category2]) {
    const filtered = await api.functional.posts.posts.patch(connection, {
      body: { category_id: category.id, page: 1, limit: 100 },
    });
    typia.assert<IPageIPost>(filtered);
    for (const p of filtered.data) {
      TestValidator.equals("category_id matches")(category.id)(p.category_id);
    }
  }
  // 7. Combined filter (user_id + category_id)
  const combined = await api.functional.posts.posts.patch(connection, {
    body: { user_id: user2.id, category_id: category2.id, page: 1, limit: 100 },
  });
  typia.assert<IPageIPost>(combined);
  for (const p of combined.data) {
    TestValidator.equals("multi user match")(user2.id)(p.user_id);
    TestValidator.equals("multi category match")(category2.id)(p.category_id);
  }
  // 8. Test pagination (limit=2, page=1,2,...)
  let remaining = posts.length;
  let seen: Set<string> = new Set();
  for (let pageNum = 1; remaining > 0; ++pageNum) {
    const page = await api.functional.posts.posts.patch(connection, {
      body: { page: pageNum, limit: 2 },
    });
    typia.assert<IPageIPost>(page);
    TestValidator.equals("page limit or less")(true)(page.data.length <= 2);
    for (const post of page.data) {
      seen.add(post.id);
    }
    remaining -= page.data.length;
    // Ensure there is no duplicate post in different pages
    TestValidator.equals("no duplicates")(seen.size)(posts.length - remaining);
  }
  // 9. Ordering - if at least 2 posts, newer appears first (by created_at desc; depends on API default)
  if (posts.length > 1) {
    const firstPage = await api.functional.posts.posts.patch(connection, {
      body: { page: 1, limit: 2 },
    });
    typia.assert<IPageIPost>(firstPage);
    if (firstPage.data.length === 2) {
      const dt0 = new Date(firstPage.data[0].created_at).getTime();
      const dt1 = new Date(firstPage.data[1].created_at).getTime();
      TestValidator.predicate("order desc by created_at")(dt0 >= dt1);
    }
  }
  // 10. Negative: filter with fake user_id/category_id
  const negative = await api.functional.posts.posts.patch(connection, {
    body: { user_id: "00000000-0000-0000-0000-000000000000", page: 1, limit: 100 },
  });
  typia.assert<IPageIPost>(negative);
  TestValidator.equals("empty matches")<number>(0)(negative.data.length);
}

