import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IPost } from "@ORGANIZATION/PROJECT-api/lib/structures/IPost";
import { IComment } from "@ORGANIZATION/PROJECT-api/lib/structures/IComment";
import { IPageIComment } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIComment";
import typia from "typia";

export async function test_list_comments_with_pagination_and_filters(connection: api.IConnection): Promise<void> {
  // 1. Create two users
  const user1: IUser = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: "00000000-0000-0000-0000-000000000001",
      username: `commenter_one_${Date.now()}`,
      email: `commenterone_${Date.now()}@test.com`,
      bio: "test user one",
      password: "testpw1",
      status: "active"
    },
  });
  const user2: IUser = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: "00000000-0000-0000-0000-000000000001",
      username: `commenter_two_${Date.now()}`,
      email: `commentertwo_${Date.now()}@test.com`,
      bio: "test user two",
      password: "testpw2",
      status: "active"
    },
  });

  // 2. Create two posts by user1
  const post1: IPost = await api.functional.posts.posts.post(connection, {
    body: {
      category_id: "00000000-0000-0000-0000-00000000c001",
      title: `Post One - ${Date.now()}`,
      content: "First post content",
      user_id: user1.id,
    }
  });
  const post2: IPost = await api.functional.posts.posts.post(connection, {
    body: {
      category_id: "00000000-0000-0000-0000-00000000c002",
      title: `Post Two - ${Date.now()}`,
      content: "Second post content",
      user_id: user1.id,
    }
  });

  // 3. Create comments for both posts, alternating users
  const comments: IComment[] = [];
  // Post1: 2 for each user
  comments.push(
    await api.functional.posts.comments.post(connection, {
      body: { user_id: user1.id, post_id: post1.id, content: `C1-u1-${Date.now()}` },
    })
  );
  comments.push(
    await api.functional.posts.comments.post(connection, {
      body: { user_id: user2.id, post_id: post1.id, content: `C2-u2-${Date.now()}` },
    })
  );
  comments.push(
    await api.functional.posts.comments.post(connection, {
      body: { user_id: user1.id, post_id: post1.id, content: `C3-u1-${Date.now()}` },
    })
  );
  comments.push(
    await api.functional.posts.comments.post(connection, {
      body: { user_id: user2.id, post_id: post1.id, content: `C4-u2-${Date.now()}` },
    })
  );
  // Post2: 2 for user1, 1 for user2
  comments.push(
    await api.functional.posts.comments.post(connection, {
      body: { user_id: user1.id, post_id: post2.id, content: `P2-C1-u1-${Date.now()}` },
    })
  );
  comments.push(
    await api.functional.posts.comments.post(connection, {
      body: { user_id: user2.id, post_id: post2.id, content: `P2-C2-u2-${Date.now()}` },
    })
  );
  comments.push(
    await api.functional.posts.comments.post(connection, {
      body: { user_id: user1.id, post_id: post2.id, content: `P2-C3-u1-${Date.now()}` },
    })
  );

  // Helper
  function isDesc<T extends { created_at: string }>(arr: T[]): boolean {
    for (let i = 1; i < arr.length; ++i)
      if (arr[i - 1].created_at < arr[i].created_at) return false;
    return true;
  }

  // 4. Retrieve comments for post1
  const out1: IPageIComment = await api.functional.posts.comments.patch(connection, {
    body: { postId: post1.id },
  });
  typia.assert<IPageIComment>(out1);
  TestValidator.predicate("all comments belong to post1")(() => out1.data.every(c => c.post_id === post1.id));
  TestValidator.predicate("pagination count matches data length")(() => out1.data.length <= out1.pagination.limit);

  // 5. Retrieve comments for post1 by user2
  const out2: IPageIComment = await api.functional.posts.comments.patch(connection, {
    body: { postId: post1.id, userId: user2.id },
  });
  typia.assert<IPageIComment>(out2);
  TestValidator.predicate("out2: all are user2 on post1")(() => out2.data.every(c => c.post_id === post1.id && c.user_id === user2.id));

  // 6. Retrieve all comments by user1
  const out3: IPageIComment = await api.functional.posts.comments.patch(connection, {
    body: { userId: user1.id },
  });
  typia.assert<IPageIComment>(out3);
  TestValidator.predicate("out3: all are by user1")(() => out3.data.every(c => c.user_id === user1.id));

  // 7. Pagination on post2
  const out4a: IPageIComment = await api.functional.posts.comments.patch(connection, {
    body: { postId: post2.id, limit: 2, offset: 0 },
  });
  typia.assert<IPageIComment>(out4a);
  TestValidator.predicate("out4a: two comments for post2")(() => out4a.data.length === 2);
  const out4b: IPageIComment = await api.functional.posts.comments.patch(connection, {
    body: { postId: post2.id, limit: 2, offset: 2 },
  });
  typia.assert<IPageIComment>(out4b);
  TestValidator.predicate("out4b: remaining comments for post2")(() => out4b.data.length <= 2);
  const collectedIds = [...out4a.data.map(c => c.id), ...out4b.data.map(c => c.id)];
  TestValidator.predicate("out4 no duplicated IDs")(() => new Set(collectedIds).size === collectedIds.length);

  // 8. Sorting by created_at desc for post1
  const out5: IPageIComment = await api.functional.posts.comments.patch(connection, {
    body: { postId: post1.id, sort: "created_at:desc" },
  });
  typia.assert<IPageIComment>(out5);
  TestValidator.predicate("out5: sorted desc by created_at")(() => isDesc(out5.data));

  // 9. Negative case: non-existent postId
  const out6: IPageIComment = await api.functional.posts.comments.patch(connection, {
    body: { postId: "ffffffff-ffff-ffff-ffff-ffffffffffff" },
  });
  typia.assert<IPageIComment>(out6);
  TestValidator.predicate("out6: data empty for non-existing post")(() => Array.isArray(out6.data) && out6.data.length === 0);
  TestValidator.equals("out6: records=0 for non-existing post")({ records: 0 })( { records: out6.pagination.records } );
}

